            [(38, 1)] = new EpisodeInfo(38, 1, "One Piece Fan Letter", "One Piece Fan Letter", "As the Straw Hat Pirates reunite on the Sabaody Archipelago, the various members of the island are drawn together like pieces of a puzzle, their love of certain members of the crew driving their actions and emotions."),
            [(1, 1)] = new EpisodeInfo(1, 1, "Romance Dawn", "Romance Dawn, the Dawn of an Adventure", "Influenced by the straw-hat-wearing pirate Red-Haired Shanks, an enthusiastic young boy named Monkey D. Luffy dreams of one day becoming King of the Pirates."),
            [(1, 2)] = new EpisodeInfo(1, 2, "Romance Dawn", "They Call Him \"Straw Hat\" Luffy", "While Alvida and her pirates plunder a cruise ship, they come across a barrel containing a strange boy named Monkey D. Luffy. The timid Koby befriends Luffy, and they share their grand dreams with each other."),
            [(1, 3)] = new EpisodeInfo(1, 3, "Romance Dawn", "The Pirate King and the Master Swordsman", "In search of members for his pirate crew, Luffy with his new friend Koby arrive at Shells Town. There, they track down the renowned Pirate Hunter Zoro but run afoul of Helmeppo, son of the infamous Captain Morgan."),
            [(1, 4)] = new EpisodeInfo(1, 4, "Romance Dawn", "The First", "Luffy convinces the captive Zoro to join his pirate crew, but they must first defeat the tyrannical Captain Morgan."),
            [(2, 1)] = new EpisodeInfo(2, 1, "Orange Town", "Enter: Nami", "Nami, a street-smart girl who loves money and tangerines, makes Luffy's acquaintance. To save herself and steal a map to the Grand Line, she tricks Luffy into allowing her to turn him over to the infamous pirate Buggy the Clown."),
            [(2, 2)] = new EpisodeInfo(2, 2, "Orange Town", "Treasure", "Luffy meets a small dog guarding his late master’s store. Meanwhile, Beast Tamer Mohji, Cabaji the Acrobat, and the lion Richie show up in the streets of Orange Town to get revenge for Captain Buggy's humiliating loss."),
            [(2, 3)] = new EpisodeInfo(2, 3, "Orange Town", "Pirate Buggy \"the Clown\"", "Shockingly, it turns out Captain Buggy and Red-Haired Shanks share a common history. When the bombastic clown rips Shanks' straw hat, Luffy's rage knows no bounds."),
            [(3, 1)] = new EpisodeInfo(3, 1, "Syrup Village", "Captain Usopp", "With Nami joining as Luffy's navigator (though not as an actual member of the crew), they arrive at Syrup Village in search of a proper ship. There they meet a man claiming to be a pirate captain who also seems to have ties to Red-Haired Shanks."),
            [(3, 2)] = new EpisodeInfo(3, 2, "Syrup Village", "Captain Kuro", "After uncovering a devious plot, Usopp warns the village and Kaya, but finds his years of lying make it difficult to convince people of the truth."),
            [(3, 3)] = new EpisodeInfo(3, 3, "Syrup Village", "The Slope", "Usopp resolves that if he is to keep the peace, the town must continue to believe he is a liar, which means he cannot let Captain Kuro’s men up the slope to the village! Luffy, Zoro, and Nami aid Usopp in his plan to keep the Black Cat Pirates at bay."),
            [(3, 4)] = new EpisodeInfo(3, 4, "Syrup Village", "The Silent Man", "Captain Kuro makes his appearance, and with Zoro too injured to fight, Luffy incapacitated, and Usopp and Nami too weak to make a stand, the situation is desperate. However, Kaya herself arrives, intent on bargaining with Kuro."),
            [(3, 5)] = new EpisodeInfo(3, 5, "Syrup Village", "The Pirate \"Kuro of a Hundred Plans\"", "While the fight between Luffy and Kuro continues, Jango works to force Kaya to bequeath her family's money to the Black Cat captain. Despite the Usopp Pirates' efforts to stop the hypnotist, it seems as if the transaction is all but inevitable."),
            [(3, 6)] = new EpisodeInfo(3, 6, "Syrup Village", "For Whom the Bell Tolls", "Kuro's ultimate move attacks everything in the area, friend nor foe notwithstanding, and Luffy becomes enraged by Kuro's lack of regard for his crew."),
            [(3, 7)] = new EpisodeInfo(3, 7, "Syrup Village", "To the Sea", "The island is saved without any villagers ever knowing what almost befell their home. Kaya, grateful to the Straw Hats for what they did, gifts them something invaluable for sailing the high seas. Meanwhile, one more person joins Luffy's crew."),
            [(4, 1)] = new EpisodeInfo(4, 1, "Gaimon", "You're the Rare Breed", "With Usopp now on the crew and their new ship, the Going Merry, under their command, the Straw Hats head to an island said to hold a fabled treasure. There they encounter a strange man stuck in a box who has been looking for the same treasure."),
            [(5, 1)] = new EpisodeInfo(5, 1, "Baratie", "Enter: Sanji", "The Straw Hats happen across Zoro's former partners, Yosaku and Johnny, whose dietary dilemma reminds the pirates they still need a cook on their ship. Thus, they set sail for the sea restaurant Baratie, hoping to snag a chef."),
            [(5, 2)] = new EpisodeInfo(5, 2, "Baratie", "Pirate Commodore Don Krieg", "Luffy is on hiatus from pirating to work as a cabin boy to pay off the damage done to the Baratie. While he tries to convince Sanji to join his crew, the pirate Gin brings the infamous Don Krieg and his starving forces to the sea restaurant."),
            [(5, 3)] = new EpisodeInfo(5, 3, "Baratie", "The Oath", "As Nami steals the Going Merry, Hawk-Eye Mihawk, known far and wide as the world's greatest swordsman, arrives all the way from the Grand Line to finish off Don Krieg. However, Zoro challenges the deadly Mihawk to a duel in order to achieve his dream."),
            [(5, 4)] = new EpisodeInfo(5, 4, "Baratie", "Pearl", "Mihawk departs after defeating Zoro, and Krieg continues his assault on the Baratie. Pearl, one of Krieg's strongest men, goes on the offensive with fiery attacks and multiple shields at his disposal, so Sanji reveals his unique fighting style."),
            [(5, 5)] = new EpisodeInfo(5, 5, "Baratie", "Because of a Dream", "Sanji continues to battle Pearl, but Gin cleverly holds Chef Zeff hostage, forcing Sanji an impossible situation. Sanji's childhood memories flood his mind, and he reflects on why Zeff means so much to him."),
            [(5, 6)] = new EpisodeInfo(5, 6, "Baratie", "MH5", "In a surprising twist, Gin finishes Pearl off himself, claiming Sanji as his own target. Though still wounded, Sanji rises to the challenge and faces off with Gin. As the battle draws to a close, Don Krieg unleashes a deadly toxin into the air."),
            [(5, 7)] = new EpisodeInfo(5, 7, "Baratie", "The Stalwart War Spear", "The struggle with Krieg nears its end. Luffy's fighting spirit is unwavering, even after taking several hits from Krieg's Stalwart War Spear. Straw Hat’s strategy shifts to smashing through Krieg’s defenses and attacking the spear head-on."),
            [(5, 8)] = new EpisodeInfo(5, 8, "Baratie", "The Fourth", "As the dust of battle settles, the still poisoned Gin decides to go back to the Grand Line with Krieg, and Sanji makes the difficult decision to join Luffy's crew. Suddenly, Yosaku returns with news of where Nami took the Going Merry."),
            [(6, 1)] = new EpisodeInfo(6, 1, "Arlong Park", "Arlong Park", "As they depart the Baratie in pursuit of Nami and the Going Merry, Yosaku warns Luffy and Sanji about the vicious Arlong and his fishman underlings. Meanwhile, Usopp, Johnny, and Zoro make landfall where Nami docked the Merry."),
            [(6, 2)] = new EpisodeInfo(6, 2, "Arlong Park", "Monsters of the Grand Line", "Usopp, having challenged the fishmen, has been caught. To effectively prove her loyalty to the Arlong Pirates, Nami is left with a morbid choice."),
            [(6, 3)] = new EpisodeInfo(6, 3, "Arlong Park", "Sea Charts and Fishmen", "Luffy’s group has reached the island but are met with a shocking revelation. Unmoved by Nami’s apparent actions and her insistence that he leaves, Luffy stubbornly sticks around."),
            [(6, 4)] = new EpisodeInfo(6, 4, "Arlong Park", "Bell-mère", "Nojiko goes into detail about Nami's childhood and their adoptive mother Bell-mère, a former Marine. Despite being poor and unrelated by blood, they had a happy life on their tangerine orchard."),
            [(6, 5)] = new EpisodeInfo(6, 5, "Arlong Park", "Live", "When a Marine officer under Arlong's pay confiscates all the money Nami accumulated over the years, it becomes clear where Arlong stands on his promise."),
            [(6, 6)] = new EpisodeInfo(6, 6, "Arlong Park", "OK, LET'S STAND UP", "Nami's audible sorrows have reached the ears of a certain Straw Hat captain, and he and his crew rise to action. The villagers look on in awe as the Straw Hats boldly enter Arlong Park to face the fishmen."),
            [(6, 7)] = new EpisodeInfo(6, 7, "Arlong Park", "Chivalry vs. Fishman Karate", "Luffy’s newest move leaves him incapacitated and at the mercy of the sea while Zoro, Sanji, and Usopp desperately fight off Arlong’s top officers."),
            [(6, 8)] = new EpisodeInfo(6, 8, "Arlong Park", "My Turn", "The top officers are defeated, but as they currently are, Sanji and Zoro prove to be no match for Arlong. However, with Luffy finally free, he just may just be able to pull it off. Meanwhile, Nami finally gathers the courage to face Arlong."),
            [(6, 9)] = new EpisodeInfo(6, 9, "Arlong Park", "Happiness", "Luffy springs into action against Arlong, and their intense struggle commences! Luffy becomes enraged when he discovers how exactly Arlong had been using Nami, and he comes to a quick decision on how to finally free her of her shackles."),
            [(6, 10)] = new EpisodeInfo(6, 10, "Arlong Park", "Spin, Pinwheel, Spin", "Arlong's reign of terror is over, so the island throws a party to celebrate their freedom and to honor their pirate saviors. During the festivities, Nami makes her decision to officially join the Straw Hat Pirates."),
            [(7, 1)] = new EpisodeInfo(7, 1, "The Adventures of Buggy's Crew", "The Adventures of Buggy's Crew", "Buggy, after being defeated by Luffy, Zoro, and Nami, begins his quest to regain his body parts and his crew, and then he will get his flashy revenge on Luffy."),
            [(8, 1)] = new EpisodeInfo(8, 1, "Loguetown", "The Town of Beginnings and Endings", "Touting a fresh bounty issued by Marine HQ, Luffy and co. make landfall at Loguetown, the place where Gold Roger was both born and executed. Being the last stop before the Grand Line, the crew stocks up on supplies and sightsees."),
            [(8, 2)] = new EpisodeInfo(8, 2, "Loguetown", "Parallels", "Usopp manages to anger the brat daughter of bounty hunter Daddy the Father and finds his own life at stake. When Daddy discovers that Usopp is the son of Red Hair Pirate Yasopp, the only man to beat him, he offers him a risky way out."),
            [(8, 3)] = new EpisodeInfo(8, 3, "Loguetown", "The Legend Has Begun", "Luffy arrives at the execution platform, only to be caught in Buggy and Alvida's trap. Sanji and Zoro rush to stop Buggy from poetically—and flashily—executing Luffy where Roger died."),
            [(10, 1)] = new EpisodeInfo(10, 1, "Reverse Mountain", "Reverse Mountain", "Following a near disaster in the Calm Belt, the Straw Hats take a wild ride up and down a mountain… and are promptly swallowed by a giant whale!"),
            [(10, 2)] = new EpisodeInfo(10, 2, "Reverse Mountain", "Cape Promise", "After learning of Laboon the whale's plight, Luffy comes up with a solution befitting his style. As thanks, Crocus reveals that navigating the Grand Line will not be as simple as following a compass or relying on currents and wind."),
            [(12, 1)] = new EpisodeInfo(12, 1, "Whisky Peak", "The Town of Celebration", "As the Straw Hat Pirates travel from Reverse Mountain to Whisky Peak, Nami adjusts to having to watch the log pose constantly. When they arrive at their destination, the townspeople greet them warmly, despite the sight of their Jolly Roger."),
            [(12, 2)] = new EpisodeInfo(12, 2, "Whisky Peak", "The Secret Criminal Organization", "As Mr. 5 and Miss Valentine chase after the fleeing princess Vivi, Igaram begs Zoro to save her. But just when Zoro is about to take on Vivi's assailants, an enraged Luffy confronts him under a massive misunderstanding of the swordsman's latest actions."),
            [(12, 1)] = new EpisodeInfo(12, 1, "Whisky Peak", "The Town of Welcome", "As the Straw Hat Pirates travel from Reverse Mountain to Whisky Peak, Nami adjusts to having to watch the log pose constantly. When they arrive at their destination, the townspeople greet them warmly, despite the sight of their Jolly Roger."),
            [(12, 2)] = new EpisodeInfo(12, 2, "Whisky Peak", "The Secret Criminal Organization", "As Mr. 5 and Miss Valentine chase after the fleeing princess Vivi, Igaram begs Zoro to save her. But just when Zoro is about to take on Vivi's assailants, an enraged Luffy confronts him under a massive misunderstanding of the swordsman's latest actions."),
            [(12, 3)] = new EpisodeInfo(12, 3, "Whisky Peak", "The Course", "With Vivi aboard the Going Merry, the Straw Hats are now targets of Crocodile, one of the Seven Warlords of the Sea and leader of Baroque Works. En route to their next destination, Miss All-Sunday pays the pirates a visit to give a few words of advice."),
            [(11, 1)] = new EpisodeInfo(11, 1, "The Trials of Koby-Meppo", "The Trials of Koby-Meppo", "Koby and Helmeppo are new cabin boys for the Marines, but they become unwillingly involved in an escape attempt by Morgan. Their bravery will either earn them the respect of their superiors—as well as Vice Admiral Garp—or it will spell their doom."),
            [(13, 1)] = new EpisodeInfo(13, 1, "Little Garden", "Adventure on Little Garden", "The Straw Hats arrive at their destination and discover that the island is home to enormous creatures... and people. Meanwhile, the Baroque Works agents Mr. 3 and his partner Miss Golden Week receive new orders."),
            [(13, 2)] = new EpisodeInfo(13, 2, "Little Garden", "Dorry and Brogy", "Usopp, immensely impressed with the giants of Elbaph, names Brogy his master. To commemorate their duel, the giants imbibe two barrels of booze from Nami, but, unbeknownst to anyone, a saboteur has tampered with one of them!"),
            [(13, 3)] = new EpisodeInfo(13, 3, "Little Garden", "Foul Play", "Despite his internal injuries and Luffy's warnings, the prideful giant Dorry insists on continuing his duel with Brogy. Meanwhile, the Baroque Works officer agents begin to make their move on the island with the Straw Hats as their next targets."),
            [(13, 4)] = new EpisodeInfo(13, 4, "Little Garden", "Luffy vs. Mr. 3", "Luffy, Usopp, and Karoo launch headlong into rescuing their friends, but Miss Golden Week runs a colorful interference. Mr. 3 is forced to take matters into his own conniving hands, and Usopp brainstorms how he will finally free his friends."),
            [(13, 5)] = new EpisodeInfo(13, 5, "Little Garden", "Straight Ahead!!", "Sanji, pretending to be Mr. 3, convinces Crocodile he has eliminated Vivi and co., prompting the Unluckies to arrive and deliver a way off Little Garden. With a clear path straight ahead—or so it seems—the Straw Hats set sail."),
            [(14, 1)] = new EpisodeInfo(14, 1, "Drum Island", "Tin Plate Wapol", "En route to Arabasta, Nami falls ill. Without a doctor aboard the Going Merry, the Straw Hats must decide whether to stay the course or find more immediate help. As their ship crosses into a frigid area, a figure is seen standing on the water's surface."),
            [(14, 2)] = new EpisodeInfo(14, 2, "Drum Island", "Adventure in a Nameless Country", "If the Straw Hats have any chance of saving Nami, they must carry her up a mountain to see the sole doctor of a nameless, winter island. While Sanji and Luffy fight off the wilderness, a despicable king makes an unwanted return."),
            [(14, 3)] = new EpisodeInfo(14, 3, "Drum Island", "The Summit", "A devastating avalanche sweeps Drum Island and puts everyone at risk, but Luffy—step-by-step, hand-over-hand—continues his death-defying ascent up the mountain. Meanwhile, Vivi, Usopp, and Zoro reunite in the wake of the disaster."),
            [(14, 4)] = new EpisodeInfo(14, 4, "Drum Island", "Enter: Tony Tony Chopper", "Enter Chopper: a talking, transforming blue-nosed reindeer who was trained by Dr. Kureha in medicine. The Straw Hats immediately see the positives and want him on their crew, but Chopper must first deal with the monstrous pain in his heart."),
            [(14, 5)] = new EpisodeInfo(14, 5, "Drum Island", "Quack Doctor", "Chopper began life as an ordinary reindeer but was shunned by his own kind because of his unusual blue nose. Even after eating the Human-Human Devil Fruit, the islanders wanted nothing to do with him. That is, until Chopper met a certain quack doctor."),
            [(14, 6)] = new EpisodeInfo(14, 6, "Drum Island", "Unbreakable", "Wapol and his comrades reach the mountaintop castle to wrest control of it back from Dr. Kureha and Chopper. Luffy, Sanji, and Chopper go head-to-head against his Munch-Munch powers, vowing to keep Wapol and his men from bringing down Hiriluk's flag."),
            [(14, 7)] = new EpisodeInfo(14, 7, "Drum Island", "The Skies of Drum", "Chopper counters the combined Chessmarimo with an item that expands his zoan fruit abilities. Wapol slips away into his old castle, but Luffy runs after him to force a final showdown."),
            [(14, 8)] = new EpisodeInfo(14, 8, "Drum Island", "Hiriluk's Cherry Blossoms", "Finally convinced to join the Straw Hat Pirates, Chopper announces his intentions to Dr. Kureha. While she doesn’t receive the news well at first, she decides to give the Straw Hats a fond parting gift while they exit the island."),
            [(15, 1)] = new EpisodeInfo(15, 1, "Arabasta", "Sir Crocodile, the Pirate", "In Arabasta, the hero Crocodile appears and puts a decisive end to a pillaging band of pirates. Meanwhile, Mr. 2 Bon Kurei pays a visit to the newly arrived Straw Hats. When he puts his abilities on display, Vivi notices something shocking."),
            [(15, 2)] = new EpisodeInfo(15, 2, "Arabasta", "Landfall in Alabasta", "The Merry makes landfall at Arabasta’s port town, and while the rest of the Straw Hats wisely don disguises, Luffy runs afoul of an old foe. However, a chance reunion with a flame-wielding member of Luffy’s family might just be the saving grace he needs!"),
            [(15, 3)] = new EpisodeInfo(15, 3, "Arabasta", "Adventure in the Kingdom of Sand", "The Straw Hats finally start their arduous trek across the deserts of Arabasta! Along the way, they encounter martial-arts-loving dugongs, avian con artists, a giant carnivorous reptile, and a camel with very particular tastes in his passengers."),
            [(15, 4)] = new EpisodeInfo(15, 4, "Arabasta", "Yuba, the Town of Rebels", "Vivi and co. finally reach Yuba, the last known location of the Rebel Army, but there is nary a soul in sight. Meanwhile, at the behest of their hidden leader, Baroque Works' officer agents begin to gather at the Spiders Cafe."),
            [(15, 5)] = new EpisodeInfo(15, 5, "Arabasta", "I Love My Country", "At Rain Dinners, the Baroque Works officer agents are finally let on to Mr. 0's true identity. And just outside Yuba, Luffy confronts a stubborn Vivi to remind her who else is willing to stand with her."),
            [(15, 6)] = new EpisodeInfo(15, 6, "Arabasta", "Rainbase, the City of Dreams", "Luffy's band reaches the city of Rainbase but encounter Smoker and Tashigi. As they defend themselves against both the Marines and Baroque Works' millions, Luffy's party storms Crocodile's Rain Dinners Casino."),
            [(15, 7)] = new EpisodeInfo(15, 7, "Arabasta", "It Begins", "Luffy leads a daring—but foolish—raid on Crocodile’s casino. His poor planning lands him behind bars with Captain Smoker. But more importantly, these actions leave Vivi alone and unprotected."),
            [(15, 8)] = new EpisodeInfo(15, 8, "Arabasta", "Rebellion", "“The king of Arabasta” appears to order Nanohana’s attack. With this, the people's anger explodes! Meanwhile, Vivi frantically looks for Sanji, and as Crocodile leaves the pirates to their fate, he is suddenly contacted by a \"Mr. Prince.\""),
            [(15, 9)] = new EpisodeInfo(15, 9, "Arabasta", "Rush!!", "Thanks to Sanji's brave actions, the Straw Hats made their escape, and Captain Smoker graciously looks the other way as they flee. However, as they set out toward Alubarna, Crocodile waylays Luffy, and the two begin a vicious duel."),
            [(15, 10)] = new EpisodeInfo(15, 10, "Arabasta", "Showdown at Alubarna", "Vivi’s party speeds along toward Alubarna, as does the Rebel Army. While the Supersonic Duck Squad helps Zoro and the others play decoy, Vivi sets out on her own to try and stop Koza and his rebels."),
            [(15, 11)] = new EpisodeInfo(15, 11, "Arabasta", "Molehill Zone Four", "Despite his severe injuries, Karoo picks up Vivi to flee the attacks of Mr. 2, and they dash madly through the battlefield. And at the southeast gate: Chopper and Usopp vs. Mr. 4 and Miss Merry Christmas!"),
            [(15, 12)] = new EpisodeInfo(15, 12, "Arabasta", "Okama Kenpo", "Usopp and Chopper combine their skills to finish off Mr. 4 and Miss Merry Christmas, and Sanji engages Mr. 2 in a vicious foot-to-foot struggle. Meanwhile, as a last-ditch effort to save her people, Princess Vivi gives Chaka an unprecedented order."),
            [(15, 13)] = new EpisodeInfo(15, 13, "Arabasta", "She Who Controls the Weather", "Nami combats Miss Doublefinger, whose Spike-Spike powers quickly turn their brawl into a losing battle. That is, until Usopp's invention, the Climate Baton, gives Nami a fighting chance. And at the palace, Crocodile reveals his ultimate goal."),
            [(15, 14)] = new EpisodeInfo(15, 14, "Arabasta", "Cutting Steel", "Crocodile reveals he seeks the mysterious Pluton, Nami has one last chance to stop Miss Doublefinger, and Zoro struggles against Mr. 1. As Zoro confronts certain death, he must remember something his sensei once imparted."),
            [(15, 15)] = new EpisodeInfo(15, 15, "Arabasta", "The Leaders", "Vivi reveals Crocodile's heinous plan to Koza. To stop the fighting, Koza raises the white flag but is suddenly shot. To induce even more panic, Crocodile announces that a massive bomb will be launched into the city, and he drops Vivi to her doom."),
            [(15, 16)] = new EpisodeInfo(15, 16, "Arabasta", "HOPE!!", "Luffy vs. Crocodile: Round 2! Luffy has discovered Crocodile’s weakness, but the power of the Sand-Sand Fruit is devastating regardless. Meanwhile, the remaining Straw Hats have ten minutes to find a hidden cannoneer before a bomb is launched into the city."),
            [(15, 17)] = new EpisodeInfo(15, 17, "Arabasta", "Croc-ish", "Luffy and Crocodile enter the third phase of their duel, and, without water at his disposal, Luffy is forced to improvise. Back on the surface, Vivi uses her knowledge of the city to finally pinpoint the location of the bomb's cannoneer."),
            [(15, 18)] = new EpisodeInfo(15, 18, "Arabasta", "I Will Defeat You", "Vivi finds the cannon but realizes the bomb can't be stopped so casually. Then Pell swoops in with a decisive, if tragic, solution. The final battle between Luffy and Crocodile, which has both parties driven to their utmost, reaches its conclusion."),
            [(15, 19)] = new EpisodeInfo(15, 19, "Arabasta", "Some Justice", "Igaram appears with proof of Cobra's innocence, and Koza tells the people the truth behind the war. Luffy and his crew are brought into Cobra's palace to rest, and, during this time, Igaram receives word that there are new bounties making the rounds!"),
            [(15, 20)] = new EpisodeInfo(15, 20, "Arabasta", "Vivi's Adventure", "As the Straw Hats make their escape from Arabasta, Bon Kurei and his men serve to distract Black Cage Hina of the Marines. Meanwhile, Vivi must make a speech, but, more importantly, she must decide whether to stay with the Straw Hats."),
            [(15, 21)] = new EpisodeInfo(15, 21, "Arabasta", "Stowaway", "The Straw Hats have evaded the Marines, but a new development crops up when a certain stowaway makes her presence on the Going Merry known… and she wants to be part of the crew!"),
            [(16, 1)] = new EpisodeInfo(16, 1, "Jaya", "Why the Log Pose Is Spherical", "When a galleon-size ship falls from the sky and Nami's log pose begins to point straight up, the Straw Hats sense a new adventure on the horizon. Right after a mysterious map is pulled from the wreckage, a group of salvagers come to claim the fallen ship."),
            [(16, 2)] = new EpisodeInfo(16, 2, "Jaya", "Never Dream", "Mock Town on western Jaya openly welcomes pirates, but that fact is accompanied by constant danger. While asking about the sky island, Luffy and Zoro have a run-in with the loud pirate Bellamy the Hyena who ridicules their dreams."),
            [(16, 3)] = new EpisodeInfo(16, 3, "Jaya", "People's Dreams", "After a one-sided brawl with Bellamy, the Straw Hats meet a black-haired pirate outside the bar who reaffirms their dreams and insists the sky island is real. As they depart for the other side of Jaya, another salvager of the Saruyama Alliance appears."),
            [(16, 4)] = new EpisodeInfo(16, 4, "Jaya", "Noland the Liar", "The Straw Hats meet Montblanc Cricket, whose descendant was none other than Montblanc Noland, an infamous \"liar\" who told of a city of gold on Jaya. Cast out for looking for artifacts from the fabled city, he may just know a way to get to Skypiea."),
            [(16, 5)] = new EpisodeInfo(16, 5, "Jaya", "Let's Eat", "Cricket explains how the Straw Hats can ride a dangerous vertical sea current to get to Skypiea. However, they must first catch a south bird to steer them toward the point where the stream will erupt from the ocean."),
            [(16, 6)] = new EpisodeInfo(16, 6, "Jaya", "The Hundred Million Man", "While the Straw Hats are in the forest attempting to catch a south bird, Bellamy and his crew attack Masira, Shoujou, and Cricket and steal every gold artifact they had collected over the years from the salvage work of the Saruyama Alliance."),
            [(16, 7)] = new EpisodeInfo(16, 7, "Jaya", "The Highest Authority in the World", "The duel between Bellamy and Luffy shocks all who look on. More shocking are the movements of fearsome pirates on the Grand Line and the machinations of the Blackbeard Pirates, as their captain vies for a position as a Warlord."),
            [(16, 8)] = new EpisodeInfo(16, 8, "Jaya", "The Knock-Up Stream", "Luffy returns victorious from his showdown with Bellamy, and the Going Merry has been refit by the Saruyama Alliance to handle its upcoming upward flight. The Straw Hats follow their captive south bird and steel themselves for their journey to the sky."),
            [(17, 1)] = new EpisodeInfo(17, 1, "Skypiea", "Heaven's Gate", "The Straw Hats, having used the Knock-Up Stream to fly into the sky, arrive on a white ocean above the clouds. While they marvel at the otherworldly sights, they are attacked by a powerful masked figure, and a mysterious flying knight comes to their aid."),
            [(17, 2)] = new EpisodeInfo(17, 2, "Skypiea", "Angel Beach", "After bidding farewell to Gan Fall and passing through the gate to Skypiea, the pirates encounter a family of Skypieans: Conis and her father, Pagaya. The two explain the workings of life in the sky and introduce the crew to the different dials."),
            [(17, 3)] = new EpisodeInfo(17, 3, "Skypiea", "Divine Reckoning", "Nami takes the waver out for a test ride and discovers the forbidden Upper Yard. Meanwhile, law enforcement arrives and rudely harasses the rest of the gang."),
            [(17, 4)] = new EpisodeInfo(17, 4, "Skypiea", "Ordeal", "The pirates are fed up and ready to leave Skypiea, but Nami, Zoro, Robin, and Chopper are whisked away aboard the Merry. The remaining Straw Hat Pirates must face Enel’s challenge, so Conis shows them the way to Upper Yard."),
            [(17, 5)] = new EpisodeInfo(17, 5, "Skypiea", "Adventure on God's Island", "Aboard the Little Crow, Luffy, Usopp, and Sanji enter Upper Yard and must choose an ordeal. Meanwhile, Zoro and co. have their own troubles at the Sacrificial Altar. When they leave to explore the forest, Chopper is left to repair the Merry."),
            [(17, 6)] = new EpisodeInfo(17, 6, "Skypiea", "The Village Hidden in the Clouds", "Luffy, Sanji, and Usopp are locked in a life-or-death struggle against the Priest Satori. It's all they can do to withstand the random attributes of Satori's strange spheres and to overcome the power of mantra, which can seemingly predict their movements."),
            [(17, 7)] = new EpisodeInfo(17, 7, "Skypiea", "Overture", "Gan Fall is severely burned during his battle with Shura, and Chopper musters the courage for a leap of faith. Elsewhere, Luffy and co. finally discover a way to defeat the mantra-wielding Satori, and the Shandians prepare for an all-out attack on God!"),
            [(17, 8)] = new EpisodeInfo(17, 8, "Skypiea", "Vearth", "The Shandians’ attack on Upper Yard commences! At the Sacrificial Altar, the crew is finally reunited, and everyone recounts what has happened. Chopper looks after the injured Gan Fall, and the pirates devise a plan to steal the gold on the island."),
            [(17, 9)] = new EpisodeInfo(17, 9, "Skypiea", "Dial Battle", "The crew splits up into Nami's escape team and Luffy's exploration team. Unfortunately, a giant snake attacks and separates Luffy's team. To top it all off, the Priests have set up their ordeals across the whole island."),
            [(17, 10)] = new EpisodeInfo(17, 10, "Skypiea", "Pirate Luffy vs. Berserker Wyper", "Only 80 remain in the Upper Yard survival game. Zoro finds himself face-to-face with the Shandian Braham, Luffy takes on Wyper and his burning bazooka, and Nami’s group on the Merry has the deadliest encounter of all!"),
            [(17, 11)] = new EpisodeInfo(17, 11, "Skypiea", "Pirate Chopper vs. Priest Gedatsu", "Luffy's fight with Wyper lands him in a dark cavern filled with ruins, Enel leaves a deadly pair of new enemies to deal with Nami and Gan Fall, and Chopper comes face to face with Sky Punk Gedatsu."),
            [(17, 12)] = new EpisodeInfo(17, 12, "Skypiea", "Pirate Robin vs. Commander Yama", "Enel wipes out several fighters regardless of their allegiance. An hour remains until God's deadline, and only twenty-four survivors remain. Meanwhile, after a deadly struggle with the rotund Yama, Robin makes the discovery of a lifetime."),
            [(17, 13)] = new EpisodeInfo(17, 13, "Skypiea", "Concerto", "While Luffy remains lost in a strange, dark cavern, at the Upper Ruins, Priest Ohm confronts Zoro, Gan Fall, and Wyper with his Ordeal of Iron. As the fight wears on, Shandian warriors, Nami, and Aisa make a sudden appearance."),
            [(17, 14)] = new EpisodeInfo(17, 14, "Skypiea", "Pirate Zoro vs. Priest Ohm", "After being swallowed by the giant snake, Nami, Gan Fall, and Aisa meet someone they never expected would be in the serpent's stomach. As Priest Ohm’s Ordeal of Iron comes to a close in the Upper Ruins, Zoro unleashes a powerful new technique."),
            [(17, 15)] = new EpisodeInfo(17, 15, "Skypiea", "Quintet", "The lost ruins of Shandora are revealed in a spectacle of lightning, and the remaining survivors of Enel's twisted game begin their showdown with the self-styled deity. As the group faces him, \"God\" reveals his ultimate goal..."),
            [(17, 16)] = new EpisodeInfo(17, 16, "Skypiea", "Conis", "God Enel reveals his grand masterpiece to the sole survivor of his deadly game, and a certain rubber man finally frees himself of the snake's stomach! Meanwhile, Conis makes a desperate dash to Angel Island to warn its residents of their impending doom."),
            [(17, 17)] = new EpisodeInfo(17, 17, "Skypiea", "Deathpiea", "As the citizens of the sky run for their lives, Enel activates Deathpiea! Luffy must discover a way to lay hands on the heinous holy man before he's sent overboard, and Sanji and Usopp mount a midair rescue mission."),
            [(17, 18)] = new EpisodeInfo(17, 18, "Skypiea", "On the Front Line of Love's Salvation", "Usopp arrives just in time to save Nami from a lethal thunderbolt, and Sanji stealthily searches for a way to sabotage the ark. Despite being slowed by an arm encased in gold, Luffy races up Giant Jack on a mission to settle the score!"),
            [(17, 19)] = new EpisodeInfo(17, 19, "Skypiea", "Godkiller", "As Enel rains destruction upon Skypiea, Wyper remembers the tales passed down by the village elder about when his ancestor the warrior Kalgara met the explorer Mont Blanc Noland."),
            [(17, 20)] = new EpisodeInfo(17, 20, "Skypiea", "The Light of Shandora", "The flashback of the legend of Mont Blanc Noland and Kalgara the warrior continues with the story of how these two very different men suddenly found friendship... and then lost it just as quickly!"),
            [(17, 21)] = new EpisodeInfo(17, 21, "Skypiea", "Raigō", "Mont Blanc Noland's tale ends in tragedy and misunderstanding. In the present, Enel strikes Angel Island with a devastating lightning bolt, and Luffy refuses to halt his climb up the beanstalk until he rings the golden bell!"),
            [(17, 22)] = new EpisodeInfo(17, 22, "Skypiea", "Love Song", "While Enel continues his thunderous barrage, Nami hatches a plan to give Luffy one more shot at a grudge match with God. With a nudge from Zoro, Wyper, and a snake, Straw Hat launches himself into the center of a sinister storm!"),
            [(17, 23)] = new EpisodeInfo(17, 23, "Skypiea", "Fantasia", "In the aftermath of Luffy's epic battle with Enel, sunny skies chase away the storms, and the cloud-dwelling citizens of Angel Island turn their attention to rebuilding their homeland."),
            [(17, 24)] = new EpisodeInfo(17, 24, "Skypiea", "I Made It Here", "Robin deciphers the message of the poneglyph, ending the Shandians' centuries-long task of guarding the golden bell. Before leaving Skypiea, the Straw Hats embark on their promised gold heist, and the people of the sky see them off on their journey."),
            [(17, 25)] = new EpisodeInfo(17, 25, "Skypiea", "Finale", "With a little help from their new friends—and a giant octopus—the Straw Hats bid farewell to Skypiea and begin their descent back to the Blue Sea. Then as night falls, a certain Warlord makes an appearance at Jaya..."),
            [(18, 1)] = new EpisodeInfo(18, 1, "Long Ring Long Land", "Adventure on a Long Island", "As they continue to traverse the Grand Line, the Straw Hats pass a ship with no sails, no Jolly Roger, and no captain. After an encounter with giant sea monkeys, they come upon an island where everything is much longer than usual."),
            [(18, 2)] = new EpisodeInfo(18, 2, "Long Ring Long Land", "Donut Race!!", "Luffy accepts Foxy’s challenge to a Davy Back Fight, a pirate contest in which the stakes are the crews themselves! Round #1 is the Donut Race, where each team races around the island in tiny boats made from just the scraps of three barrels."),
            [(18, 3)] = new EpisodeInfo(18, 3, "Long Ring Long Land", "Groggy Ring!!", "Unexpected interference from Foxy himself cost the Straw Hats more than they bargained for. Now, in a struggle against literal monsters, the unlikely duo of Sanji and Zoro must win round #2, the Groggy Ring."),
            [(18, 4)] = new EpisodeInfo(18, 4, "Long Ring Long Land", "Combat", "The two captains face off in a final game: a battle of brawn aboard Foxy's ship in round #3, Combat! Luffy, sporting a killer afro and an attitude to match, steps into the boxing ring to put the hurt on Foxy."),
            [(18, 5)] = new EpisodeInfo(18, 5, "Long Ring Long Land", "K.O.", "The final round of the Davy Back Fight rages on, and atop the deck of Foxy's ship, the epic battle nears its climax. During the fight, Luffy makes a surprise discovery about Foxy's powers that he keeps strategically hidden."),
            [(18, 6)] = new EpisodeInfo(18, 6, "Long Ring Long Land", "Marine HQ Admiral Aokiji", "Only three officers in all the Marines hold the position of admiral, literal powerhouses of the World Government. The icy Aokiji, one such admiral, catches up with the Straw Hats, but Luffy is not the one he is after."),
            [(19, 1)] = new EpisodeInfo(19, 1, "Water Seven", "The City of Water, Water Seven", "The keepers of Shift Station, Kokoro and her granddaughter Chimney, point the Straw Hats in the direction of the metropolitan Water Seven. The nearby city is regarded as the best place to find a decent shipwright for the Going Merry."),
            [(19, 2)] = new EpisodeInfo(19, 2, "Water Seven", "Mr. Iceburg", "The Straw Hats cash in their Skypiean gold and meet up with Iceburg, the celebrity mayor of Water Seven, to see about an appraisal for their ship's repairs. While the foreman Kaku checks the Merry, dubious people eye the pirates' money."),
            [(19, 3)] = new EpisodeInfo(19, 3, "Water Seven", "The Case of the Kidnapped Pirate", "Kalifa shows Luffy and co. around Dock One and tells them about Iceburg and Galley-La Company. During the tour, Kaku returns with some bad news, and a long-nosed Straw Hat goes missing… along with the money he was watching!"),
            [(19, 4)] = new EpisodeInfo(19, 4, "Water Seven", "Impasse", "The Straw Hats raid the Franky House, but they soon find that their real target, Franky, is long gone with the stolen money. When the battered Usopp awakens, nothing can prepare him to hear what Luffy has decided about the Merry."),
            [(19, 5)] = new EpisodeInfo(19, 5, "Water Seven", "Luffy vs. Usopp", "Usopp is unwilling to say farewell to the Going Merry… not without a fight, that is! He challenges his former captain to a duel between men, so Luffy honors the pride of his former crewmate and faces him in battle."),
            [(19, 6)] = new EpisodeInfo(19, 6, "Water Seven", "Warning", "Iceburg has been shot! When Luffy and Nami decide to go check on him, the fire-breathing, half-mechanical Franky challenges the Straw Hats’ captain. Before their fight gets too heated, Galley-La swoops in with an unexpected accusation!"),
            [(19, 7)] = new EpisodeInfo(19, 7, "Water Seven", "Rumors", "Things are hectic enough with Franky going after Luffy, but now the Straw Hats are being framed for a crime seemingly committed by Nico Robin. As if to put the proverbial icing on the cake, the annual storm Aqua Laguna is on its way toward Water Seven."),
            [(19, 8)] = new EpisodeInfo(19, 8, "Water Seven", "Devil", "Nico Robin appears to be working alongside a World Government shadow organization called CP9. When the Straw Hats learn about Robin's current affiliation, Luffy decides that in order to know the full truth, they must catch Robin and make her talk."),
            [(19, 9)] = new EpisodeInfo(19, 9, "Water Seven", "Cipher Pol No. 9", "Robin and her tall, mysterious, masked companion arrive at Iceburg's room, and she learns that he possesses blueprints to the ancient weapon Pluton, spoken of in the poneglyphs. Then, shockingly, the members of CP9 unmask themselves."),
            [(19, 10)] = new EpisodeInfo(19, 10, "Water Seven", "Six Powers", "Luffy's group finally catches up to Robin, but they are greeted by disappointment—Robin opts to stay with CP9 and leave the Straw Hats. As Robin turns away from Luffy, the members of CP9 confronts the pirates."),
            [(19, 11)] = new EpisodeInfo(19, 11, "Water Seven", "Klabautermann", "As Usopp works to repair the Going Merry, he contends with Franky's insistence on dismantling it. During this time, Usopp reflects on who it was that conducted the ship's repairs while in Skypiea, and Franky confirms his suspicions."),
            [(19, 12)] = new EpisodeInfo(19, 12, "Water Seven", "The Legendary Shipwright", "In the past, Franky—a.k.a. Cutty Flam—was part of the legendary Tom's Workers, and he and Iceburg helped create the sea train. Spandam, a member of CP5, was determined to get Tom's blueprints for Pluton by any means necessary."),
            [(19, 13)] = new EpisodeInfo(19, 13, "Water Seven", "Mr. Tom", "The old fishman Tom was framed by CP5 for the crime of attacking the Marines with Franky's battleships. As the World Government hauled Tom off to Enies Lobby, Franky received a lasting reminder to take pride in the ships he builds."),
            [(19, 14)] = new EpisodeInfo(19, 14, "Water Seven", "Bingo", "CP9 hauls away Franky and Usopp, but not before giving a coldhearted farewell to the Merry. At Galley-La’s HQ, Nami learns from Iceburg the truth behind Robin's betrayal and the difficult choice she had to make to save the lives of the Straw Hats."),
            [(19, 15)] = new EpisodeInfo(19, 15, "Water Seven", "Departing Shortly", "While the members of Galley-La help Chopper search for Luffy and Zoro, Nami rushes to the departing sea train to keep Robin from sacrificing her freedom. At the station, a man finds Nami and hands her a letter from Sanji."),
            [(19, 16)] = new EpisodeInfo(19, 16, "Water Seven", "Aqua Laguna", "Sanji coolly fights his way through the sea train and locates Usopp and Franky. Back in Water Seven, Kokoro sees how determined Luffy is to save Robin, so she and Iceburg lend the Straw Hat crew an unexpected helping hand."),
            [(19, 17)] = new EpisodeInfo(19, 17, "Water Seven", "Sogeking", "Rocketman blasts out of Water Seven and connects with the sea tracks, and old foes become allies for the coming struggle. Aqua Laguna swells, and it's up to Luffy and Zoro to combine their might and create safe passage for all aboard."),
            [(19, 18)] = new EpisodeInfo(19, 18, "Water Seven", "Ramen Kenpo", "Sogeking appears in grand fashion! Wanze threatens Sanji's group with his ramen kenpo, so the Straw Hat cook urges the others ahead. Franky takes on CP9's newest recruit “Sea Weasel” Nero, and Sogeking moves forward to meet up with Robin."),
            [(19, 19)] = new EpisodeInfo(19, 19, "Water Seven", "The Honorable Captain T-Bone", "In his battle against Wanze and his strange Ramen Kenpo, Sanji makes an exception to his rule of never using his hands. Meanwhile, Franky and Nero are baffled by each other’s abilities, and back on Rocketman, Zoro clashes with “Ship-Slasher” T-Bone."),
            [(19, 20)] = new EpisodeInfo(19, 20, "Water Seven", "Necessary Evil", "Sanji and Franky conclude their fights and enter the train car where CP9’s agents lie in wait. When Sogeking sees the danger of the situation, it falls to him to formulate a plan for escape. However, CP9 is far from being thwarted!"),
            [(20, 1)] = new EpisodeInfo(20, 1, "Enies Lobby", "The Superhumans of Enies Lobby", "As the Straw Hats and their allies approach Enies Lobby on Rocketman, they hatch a plan to save Robin before she passes through the Gates of Justice. Meanwhile, Spandam holds a meeting with CP9 where they await Lucci's return in the Tower of Justice."),
            [(20, 2)] = new EpisodeInfo(20, 2, "Enies Lobby", "The Big Showdown on the Judiciary Island!!", "Luffy launches a one-man assault on Enies Lobby, and the rest of his allies work to break the first gate's defenses. What awaits Galley-La and the Franky Family at the second gate is a showdown of massive proportions!"),
            [(20, 3)] = new EpisodeInfo(20, 3, "Enies Lobby", "Express to the Main Island of Enies Lobby", "Robin and Franky are finally brought before Spandam while Luffy continues to blast through waves of marines. Galley-La and the Franky Family still have one giant to deal with, and the main gate closes to halt Rocketman's entry."),
            [(20, 4)] = new EpisodeInfo(20, 4, "Enies Lobby", "Fired", "The Straw Hats struggle to catch up to Luffy, who is already on the main island wreaking havoc for the Marines. Meanwhile, Robin learns from Spandam that the terms of their deal have changed, and he somehow has rights to a buster call."),
            [(20, 5)] = new EpisodeInfo(20, 5, "Enies Lobby", "Raise the Counterattack Signal", "Luffy makes it just outside the courthouse when Blueno of CP9 appears before him, and on the main road, the Straw Hats near the courthouse plaza aboard Sodom and Gomorrah. Meanwhile, Sogeking comes across a dejected Oimo."),
            [(20, 6)] = new EpisodeInfo(20, 6, "Enies Lobby", "Unprecedented", "Kaku and Kalifa of CP9 are offered a rare chance for new powers and are each served a devil fruit. The Straw Hats finally arrive at the courthouse plaza when Spandam receives an urgent call, and he realizes things are much worse than previously reported."),
            [(20, 7)] = new EpisodeInfo(20, 7, "Enies Lobby", "Gear Second", "The Straw Hats storm the courthouse where the three-headed judge Baskerville awaits to enact violent judgment upon them. On the roof, Luffy thanks CP9's Blueno for the strength of his opponents, and he activates a new ability."),
            [(20, 8)] = new EpisodeInfo(20, 8, "Enies Lobby", "The Girl They Called a Devil", "Twenty years ago, Nico Robin grew up on Ohara in the West Blue, striving to become an archaeologist like her mother. When Robin met Saul, a giant who washed onto the shores of Ohara, her life would forever be changed."),
            [(20, 9)] = new EpisodeInfo(20, 9, "Enies Lobby", "Ohara vs. The World Government", "Robin's mother Olvia was on the run from the World Government, and tragedy was unavoidable from the moment Professor Clover and his colleagues decided to continue their research. No one on Ohara was safe."),
            [(20, 10)] = new EpisodeInfo(20, 10, "Enies Lobby", "Declaration of War", "To prove his undying loyalty to Robin, Luffy makes a defiant statement to the World Government."),
            [(20, 11)] = new EpisodeInfo(20, 11, "Enies Lobby", "Jump into the Waterfall!!", "Franky stages his own rebellious act against Spandam while Luffy's crew makes a daring leap of faith to hopefully gain entrance to the Tower of Justice."),
            [(20, 12)] = new EpisodeInfo(20, 12, "Enies Lobby", "Pirates vs. CP9", "The Straw Hats split up to find the correct key before Robin’s fate is sealed. Zoro finds and engages Kaku in battle, and Sogeking attempts to steal a key from the sleeping Jabra."),
            [(20, 13)] = new EpisodeInfo(20, 13, "Enies Lobby", "Mr. Chivalry", "Sanji faces his greatest weakness: fighting a beautiful woman! Meanwhile, Franky begins to run low on cola energy, but he must battle the peculiar Fukuro to obtain more of his refreshing fuel from the kitchen."),
            [(20, 14)] = new EpisodeInfo(20, 14, "Enies Lobby", "Vitality Response", "Refreshed and refueled, Franky turns the tide in his battle with Fukuro. Meanwhile, Zoro and Sogeking must find a way to compensate for their self-imposed handicap, and Chopper unfortunately comes face-to-face with Kumadori."),
            [(20, 15)] = new EpisodeInfo(20, 15, "Enies Lobby", "Monster", "Chopper's abuse of his rumble balls results in a monstrous side effect. In the meantime, Nami learns how Kalifa's new devil fruit powers sapped Sanji of his strength, and a determined Luffy finally catches up to Spandam, Lucci and Robin."),
            [(20, 16)] = new EpisodeInfo(20, 16, "Enies Lobby", "The Terrifying Emergency Broadcast", "As the Marines eke out a comeback against the Straw Hats’ allies, Spandam makes the call Robin most dreaded. In the meantime, Lucci and Luffy begin their mutually anticipated duel, while Nami and Kalifa's fight enters the next phase."),
            [(20, 17)] = new EpisodeInfo(20, 17, "Enies Lobby", "The Hunter", "Kalifa attempts to counter Nami’s mirages, and something must be done to save Chopper, still on a monstrous rampage. Zoro, now uncuffed, resumes his duel with Kaku. As Sogeking reaches his limit against Jabra, Sanji reappears, and he is hunting wolves."),
            [(20, 18)] = new EpisodeInfo(20, 18, "Enies Lobby", "Sanji vs. Jabra", "As Nami drags the wounded Sogeking from the fight to go find Robin, he suddenly counters with a better idea. In the meantime, Sanji faces off against Jabra's Iron Body Kenpo, and, to break the latter's defenses, Sanji unveils his latest technique."),
            [(20, 19)] = new EpisodeInfo(20, 19, "Enies Lobby", "Zoro vs. Kaku", "Spandam drags Robin along the Bridge of Hesitation and finally arrives at the Gates of Justice. Meanwhile, Zoro continues his clash with Kaku, and as things become more desperate, Zoro's rage begins to manifest as a dark aura."),
            [(20, 20)] = new EpisodeInfo(20, 20, "Enies Lobby", "Buster Call", "Luffy gives Franky the opening he needs to head for Robin. The cyborg runs into trouble at the start of the bridge, but all is not lost as a lone sniper appears atop the Tower of Justice. Despite these minor victories, the buster call commences."),
            [(20, 21)] = new EpisodeInfo(20, 21, "Enies Lobby", "Rob Lucci", "With time running out, Luffy activates another special ability. The vice admirals of the buster call are shocked to see Luffy taking on Rob Lucci and recall how the CP9 enforcer, even in his youth, held strongly to a dark sense of justice."),
            [(20, 22)] = new EpisodeInfo(20, 22, "Enies Lobby", "The Legend of the Mermaid", "Lucci corners Luffy, weakened by his new ability’s aftereffects. Back on the bridge, Spandam makes an attempt on Robin's life, so Franky jumps into action. Meanwhile, the rest of the gang is about to drown in the flooded passageway."),
            [(20, 23)] = new EpisodeInfo(20, 23, "Enies Lobby", "We're Not in Hell", "The Straw Hats watch as Enies Lobby burns, and Franky fears for his men when he hears of a successful attack against them. Luffy resumes his fight against Lucci while 200 Marine captains bear down on his crewmates."),
            [(20, 24)] = new EpisodeInfo(20, 24, "Enies Lobby", "Utter Defeat", "Luffy has finally defeated Rob Lucci, and good news arrives that the Franky Family and Galley-La foremen are all alive and well. As the Straw Hats scramble to escape the Marines, they hear a voice telling them to jump into the sea."),
            [(20, 25)] = new EpisodeInfo(20, 25, "Enies Lobby", "A Gentle Snowfall of Remembrance", "Enies Lobby and the buster call fleet are out of sight, but the Going Merry begins to break apart. The Straw Hats are intercepted by Iceburg aboard his company ship and discover how the Merry found her way back to them one last time."),
            [(21, 1)] = new EpisodeInfo(21, 1, "Post-Enies Lobby", "The Strongest Family Lineage?! The Vice-Admiral and The Revolutionary!", "Two days later in Water Seven, Franky hears that the item for he purchased with the Straw Hats' stolen money has arrived. While the pirates recover from the events at Enies Lobby, a Marine vice admiral arrives to pay Luffy an unexpected visit."),
            [(21, 2)] = new EpisodeInfo(21, 2, "Post-Enies Lobby", "The Extent of the Grand Line and The 600,000,000 Crew!", "In the New World, not even the Marines' best can keep Red-Haired Shanks from meeting Whitebeard with an especially important request. Meanwhile, at Water Seven, the Franky Family comes to the Straw Hats with some \"bad\" news."),
            [(21, 3)] = new EpisodeInfo(21, 3, "Post-Enies Lobby", "Forgive Yourself! Franky Departs!", "The Straw Hats have barely had time to marvel at their new bounties when a new surprise awaits them: the unveiling of their magnificent new ship. The next step is bringing a shipwright into their ranks, and there is only one man for the job."),
            [(21, 4)] = new EpisodeInfo(21, 4, "Post-Enies Lobby", "The Astonishing Thousand Sunny and Usopp's Tearful Apology!", "The Straw Hats set sail, but Usopp is conspicuously missing. Zoro soberly reminds Luffy that Usopp's return should not be granted so casually. More immediately, the pirates need to avoid Garp’s barrage of cannon balls!"),
            [(21, 5)] = new EpisodeInfo(21, 5, "Post-Enies Lobby", "The Circling Bounties and The Duel on Banaro Island!", "The Straw Hats’ new wanted posters begin circulating around the world, but three days earlier, Blackbeard began plotting their pursuit. However, one obstacle stood in his way; Ace had finally caught up to his estranged crewmate!"),
            [(22, 1)] = new EpisodeInfo(22, 1, "Thriller Bark", "The Mysterious Sea and The Skeleton Brook!", "The Thousand Sunny gets flung into the foggy Florian Triangle and the Straw Hats encounter a ruined ship. Speculating as to whether there are ghosts aboard, it turns out the pirates are not far from the truth."),
            [(22, 2)] = new EpisodeInfo(22, 2, "Thriller Bark", "Adventure on the Ghost Island and the Famous Doctor Hogback!", "Brook bids the Straw Hats farewell, and they decide to explore the nearby ghost island Thriller Bark. Freakish danger awaits the landing party the moment they arrive, and, back on the Sunny, the rest of the crew is harassed by an unseen foe."),
            [(22, 3)] = new EpisodeInfo(22, 3, "Thriller Bark", "The Invisible Man and the Secret of the Mansion!", "While staying at Hogback's eerie mansion, Nami is assaulted by an invisible figure in the shower. It does not end there as Usopp battles haunted paintings with a handful of candles. Meanwhile, the rest of the Straw Hats finally make landfall."),
            [(22, 4)] = new EpisodeInfo(22, 4, "Thriller Bark", "General Zombie Night! Thriller Bark's Mysterious Four", "The Straw Hats learn that the head honcho on the island is one of the Seven Warlords, and he has concocted a shadowy scheme. As Absalom rallies the zombies, the master of Thriller Bark awakens."),
            [(22, 5)] = new EpisodeInfo(22, 5, "Thriller Bark", "Perona's Wonder Garden and the Charge of the General Zombies!", "Nami, Usopp, and Chopper manage to escape their coffin but find themselves in a garden surrounded by animal zombies, including one acting remarkably like Sanji. It also seems that one by one, the Straw Hats are disappearing."),
            [(22, 6)] = new EpisodeInfo(22, 6, "Thriller Bark", "Number 900?! Lola's Pursuit and the Humming Swordsman", "Lola attacks Nami out of ignorant jealousy, but the Sanji-esque zombie plays interference. Meanwhile, with Luffy captured, and Franky and Robin left to face a massive spider monkey, a familiar bony figure falls from the sky."),
            [(22, 7)] = new EpisodeInfo(22, 7, "Thriller Bark", "The Fearsome Ability of the Shadow Fruit! The Ultimate Number 900!", "Gecko Moria takes his next step toward his goal of eventually becoming king of the pirates by creating the ultimate zombie. Luffy's shadow in hand, Moria begins the revival of the much-anticipated number 900."),
            [(22, 8)] = new EpisodeInfo(22, 8, "Thriller Bark", "Oars' Awakening and Brook vs. Ryuma!", "Dead for 500 years and left in a deep freeze since, Oars awakens, fueled by Luffy's shadow. Soon after, Nami, Usopp, and Chopper are discovered hiding inside Perona's pet bear, and Absalom abducts Nami for his demented marriage ceremony."),
            [(22, 9)] = new EpisodeInfo(22, 9, "Thriller Bark", "The Hope and Despair of the Skeleton and the Straw Hats' Counterattack!", "The Straw Hats regroup on the Sunny in a pitiable state, but not before Brook leaves Franky with some valuable information. In addition, they now know of the promise Brook made to a certain friend fifty years ago, someone the Straw Hats know well."),
            [(22, 10)] = new EpisodeInfo(22, 10, "Thriller Bark", "Already Negative! Usopp's Courageous Stand!", "Oars' entry into the fight suddenly changes everything! Then, when Perona attacks, only one of the pirates seems to be immune to her negative powers. Due to his glass-half-empty personality, Usopp might have what it takes to defeat the Ghost Princess."),
            [(22, 11)] = new EpisodeInfo(22, 11, "Thriller Bark", "A Stolen Dream! Sanji vs. Absalom", "Sanji crashes Absalom's sham of a wedding, the fire in his heart burning brightly as he rescues Nami. A curious connection between Sanji and the Clear-Clear Fruit is revealed, and he vows to defeat Absalom for ruining his lifelong dream."),
            [(22, 12)] = new EpisodeInfo(22, 12, "Thriller Bark", "Perona's Terror! Usopp vs. the Ghost Princess!", "After getting the stuffing beat out of him by a zombie teddy bear, Usopp summons his sharp-shooting alter ego to help him discover the secret to defeating Perona! He reaches deep into his devious bag of tricks to even the odds in this spooky battle."),
            [(22, 13)] = new EpisodeInfo(22, 13, "Thriller Bark", "Zoro vs. Ryuuma and Chopper's Rage!", "Zoro stands in for Brook and duels Ryuma, the swordsman zombie from faraway Wano. In the mansion, Chopper's former respect for Dr. Hogback is completely erased after coming to realize the man's twisted views on life."),
            [(22, 14)] = new EpisodeInfo(22, 14, "Thriller Bark", "Show Yourselves, Straw Hat Pirates! The Crew's Hopeless Counterattack!", "With Oars now a fully obedient zombie, Gecko Moria takes his leave while instructing the giant to hunt down the pirates, whose wanted posters are sewn onto his arm. One by one, the Straw Hats' battles are interrupted by Oars' march around Thriller Bark."),
            [(22, 15)] = new EpisodeInfo(22, 15, "Thriller Bark", "Clash! The Titanic Battle Against Zombie 900!", "The Straw Hats sans Luffy are faced with the insurmountable task of taking out Oars. To knock him down, Franky decides they will need to use his newly developed tactic. But as they begin \"docking\" onto one another, a slight problem arises."),
            [(22, 16)] = new EpisodeInfo(22, 16, "Thriller Bark", "The Mysterious Visitor! The Two Warlords Meet!", "While the Straw Hats continue to wear away at Oars, Nami confronts Perona attempting to escape on board the Thousand Sunny, but the sudden appearance of a large, mysterious figure soon makes her efforts moot."),
            [(22, 17)] = new EpisodeInfo(22, 17, "Thriller Bark", "Combination of Brains and Brawn! Oars and Moriah vs. the Straw Hats!", "To give Luffy a fighting chance against Moria, Lola and her pirate crew propose a plan to fight shadow with shadow. Meanwhile, Gecko Moria uses his abilities to enhance and manipulate Oars’ massive body."),
            [(22, 18)] = new EpisodeInfo(22, 18, "Thriller Bark", "The Crew's Annihilation! Oars vs. Nightmare Luffy", "Moria’s strange combination with Oars defeats the Straw Hats one after the other. They are at their breaking point when a powerful figure swoops in and takes up the fight in their stead. The hellish creation: Nightmare Luffy!"),
            [(22, 19)] = new EpisodeInfo(22, 19, "Thriller Bark", "Morning Comes! The Straw Hats' Ultimate Attack!", "Oars recovers from Nightmare Luffy's assault, but the Straw Hats all get back on their feet and launch a final devastating attack on Oars. Morning fast approaches, and Moria is far from finished, ready to use his ultimate technique."),
            [(22, 20)] = new EpisodeInfo(22, 20, "Thriller Bark", "The Power That Can Deflect Anything?! Zoro Fights Prepared to Die!", "Moria’s defeat returns the shadows of all his victims. However, one Warlord remains on the island, and he has orders to terminate the Straw Hats and everyone else in the area. A lone Zoro faces Kuma in vain and is presented with a deadly choice."),
            [(22, 21)] = new EpisodeInfo(22, 21, "Thriller Bark", "Brook's Past! The Rumbar Pirates and Laboon!", "Brook recalls his past, thinking back on the time when the small whale Laboon started following his pirate ship and befriended the crew."),
            [(22, 22)] = new EpisodeInfo(22, 22, "Thriller Bark", "Brook's Promise! The Eighth Member", "At death's door, the Rumbar Pirates sang \"Binks' Brew\" one last time and recorded it in a tone dial, hoping to one day deliver it to Laboon. With their new crewmate and musician Brook on board, the Straw Hats bid farewell to Lola’s crew."),
            [(23, 1)] = new EpisodeInfo(23, 1, "Sabaody Archipelago", "Flying Fish Riders", "As the Straw Hats journey to Fishman Island, they reach the Red Line, the halfway point along the Grand Line. When a sea king appears, Luffy easily dispatches it, but it spits up a mermaid and a talking starfish onto the deck of the Sunny."),
            [(23, 2)] = new EpisodeInfo(23, 2, "Sabaody Archipelago", "Iron Mask Duval", "The Straw Hats arrive at Duval's (eerily quiet) base and manage to locate Camie's captured friend. Realizing the captive’s identity, the Straw Hats reluctantly agree to rescue him, and only in exchange for directions to Fishman Island."),
            [(23, 3)] = new EpisodeInfo(23, 3, "Sabaody Archipelago", "Duval's Tragedy", "Blaming Sanji for his misfortunes, Duval swears vengeance on the Straw Hats. Meanwhile, the Flying Fish Riders attempt to sink the Sunny, but the ship is far from vulnerable. Finally, Sanji himself faces Duval in a bid to end things."),
            [(23, 4)] = new EpisodeInfo(23, 4, "Sabaody Archipelago", "Adventure on the Archipelago of Dancing Soap Bubbles", "The Straw Hats make their way to Sabaody Archipelago to prepare the Sunny for its undersea voyage to Fishman Island. During their search for a coating mechanic, the Straw Hats encounter the Celestial Dragons, the cruel rulers of the World Government."),
            [(23, 5)] = new EpisodeInfo(23, 5, "Sabaody Archipelago", "The Embers of History", "While at Shakky’s Rip-off Bar, the Straw Hats learn that Luffy, Zoro and nine other rookie pirates on Sabaody have reached a certain level of infamy. Later, Zoro has a close call with an insufferable and sadistic Celestial Dragon."),
            [(23, 6)] = new EpisodeInfo(23, 6, "Sabaody Archipelago", "The Auction House Incident", "Nami's group arrives at the Grove 1 auction house where Camie will be sold to the highest bidder. With 200 million berries in hand, Nami is determined to win back their friend. The auction wears on as Luffy and co. race to the grove."),
            [(23, 7)] = new EpisodeInfo(23, 7, "Sabaody Archipelago", "An Island in Chaos", "As chaos erupts inside the auction house, a mysterious man with an equally mysterious power emerges from backstage. But now, due to Luffy’s insolent actions, a Marine admiral is on his way to Sabaody."),
            [(23, 8)] = new EpisodeInfo(23, 8, "Sabaody Archipelago", "Roger and Rayleigh", "The Marines surround the auction house, and super rookies Monkey D. Luffy, Eustass Kid, and Trafalgar Law all want a piece of the action. Later, Rayleigh reveals his shocking past to the Straw Hats."),
            [(23, 9)] = new EpisodeInfo(23, 9, "Sabaody Archipelago", "Kizaru Arrives", "Luffy and his crew decide to lay low in town while Rayleigh coats the Sunny. Meanwhile, Admiral Kizaru from Marine HQ arrives on the archipelago in search of those responsible for threatening the Celestial Dragons."),
            [(23, 10)] = new EpisodeInfo(23, 10, "Sabaody Archipelago", "Kizaru vs. the Four Captains", "While four super rookies square off against Kizaru, the Straw Hats combine their strongest attacks to defeat the pacifista menacing them. The battle is barely won when yet another, deadlier foe comes to take its place."),
            [(23, 11)] = new EpisodeInfo(23, 11, "Sabaody Archipelago", "I Couldn't Save Them!!!", "In the face of overwhelming danger, Luffy orders a full retreat. However, Warlord Kuma cuts off their frenzied escape and uses a harrowing touch of his devil fruit powers against Luffy’s crewmates, one by one."),
            [(24, 1)] = new EpisodeInfo(24, 1, "Amazon Lily", "Adventure on the Island of Women", "Luffy finds himself alone, stranded and starving on an unfamiliar island. After a run-in with some exotic mushrooms, he is approached by a team of female warriors who could be friend... or foe."),
            [(24, 2)] = new EpisodeInfo(24, 2, "Amazon Lily", "Pirate Empress Boa Hancock", "Luffy storms the village of women in hopes of acquiring a boat. However, a misstep lands him in the personal bath of none other than the Snake Princess. As he peers through the steam rising from its waters, he sees a forbidden secret etched upon her back."),
            [(24, 3)] = new EpisodeInfo(24, 3, "Amazon Lily", "The Arena", "Luffy is sentenced to execution by arena combat with Boa Hancock’s serpentine sisters. Luffy soon discovers that the two women have a mysterious ability that allows them to slither through all his attacks unharmed."),
            [(24, 4)] = new EpisodeInfo(24, 4, "Amazon Lily", "Hancock's Confession", "Impressed by Luffy’s efforts to protect the secret of the snake sisters, Hancock reveals the story behind the familiar mark on their backs. Their childhood was tragic, suffered at the hands of a corrupt group that Luffy knows all too well."),
            [(24, 5)] = new EpisodeInfo(24, 5, "Amazon Lily", "No One Can Stop This Now", "As Luffy prepares to depart Amazon Lily, Hancock suddenly takes ill. Taking heed of the knowledge and wisdom of Elder Nyon, the Snake Princess “recovers,” and a plan to save Ace is quickly hatched."),
            [(25, 1)] = new EpisodeInfo(25, 1, "Impel Down", "The Underwater Prison, Impel Down", "To rescue his brother Ace, Luffy must infiltrate the well-guarded prison Impel Down. He eludes the watchful eyes of security for a time, and then, for better or worse, a certain red-nosed criminal appears."),
            [(25, 2)] = new EpisodeInfo(25, 2, "Impel Down", "First Son of the Sea", "After forming an unlikely alliance, Luffy rushes through Impel Down aided by Buggy's powers. Meanwhile, Ace finds himself with an unlikely companion as well when he exchanges words with a powerful pirate prisoner."),
            [(25, 3)] = new EpisodeInfo(25, 3, "Impel Down", "From One Hell to Another", "Luffy and his dubious companions face off against the mythical beasts of Level 2. Impel Down is put on lockdown, its only exit sealed, but Luffy, unaware and unafraid, continues to Ace's floor, only to have another unexpected reunion."),
            [(25, 4)] = new EpisodeInfo(25, 4, "Impel Down", "Inferno Hell", "Luffy and his deranged team pummel everything from Level 3 to Level 4 that dares to stand in their path. However, the powerful guard Minotaurus is ready to do some pummeling of his own while a boiling bath of blood awaits below."),
            [(25, 5)] = new EpisodeInfo(25, 5, "Impel Down", "Friend", "With nowhere to run, Luffy's desperation to save Ace forces him to make a very grim sacrifice against his venomous adversary. Down in the frozen depths of Level 5, Bon Kurei rushes to his friend's aid, betting on a distant hope."),
            [(25, 6)] = new EpisodeInfo(25, 6, "Impel Down", "A Ray of Hope", "Bon Kurei wanders through Level 5 in search of a mysterious missing prisoner. However, the fearsome wolves and bitter cold eventually take their toll, and he collapses in the Freezing Hell all while Luffy's life continues to dwindle away."),
            [(25, 7)] = new EpisodeInfo(25, 7, "Impel Down", "Infinite Hell", "Luffy is back in action, and he prepares to set off, now with even more powerful allies who have ties to his old man. When they reach the lowest level of Impel Down, the legendary Level 6, they are in for a few new surprises."),
            [(25, 8)] = new EpisodeInfo(25, 8, "Impel Down", "Yet Another Epic Incident", "A veritable all-star team on his side, Luffy and co. battle their way back up to Level 4. On Level 2, a massive riot erupts, plunging the prison into chaos. To further complicate matters, another infamous pirate breaks into Impel Down."),
            [(25, 9)] = new EpisodeInfo(25, 9, "Impel Down", "To Sunshine and Freedom", "It is utter chaos in Impel Down, and between the sudden appearance of Blackbeard, the vice warden’s interference, and Magellan’s dogged attempts to restore order, the escape team just might see daylight yet!"),
            [(25, 10)] = new EpisodeInfo(25, 10, "Impel Down", "Thank You", "Thanks to Jinbe’s quick thinking, Luffy and co. narrowly escape Impel Down. However, the Gates of Justice remain closed, and a horde of Marine ships is gaining on them. Then, just when all hope seems lost, a dear friend makes an all-or-nothing gamble."),
            [(26, 1)] = new EpisodeInfo(26, 1, "If You Could Go Anywhere... The Adventures of the Straw Hats", "If You Could Go Anywhere... The Adventures of the Straw Hats", "Somewhere in the world... Sanji resists, while Robin meets terrible people. Franky's not himself, and Usopp learns to survive. Chopper avoids becoming food, and Nami reports on the weather. Brook is repaid in underpants, and Zoro... is lost and annoyed."),
            [(27, 1)] = new EpisodeInfo(27, 1, "Marineford", "Marine Headquarters", "Fifty warships, 100,000 Marines, the Seven Warlords, and three admirals: Akainu, Aokiji, and Kizaru. The stage is set, and, as a battle with Whitebeard looms, Fleet Admiral Sengoku reveals a shocking truth about Ace."),
            [(27, 2)] = new EpisodeInfo(27, 2, "Marineford", "Ace and Whitebeard", "To the surprise of all, the Moby Dick surfaces in the middle of Marineford! On the deck stands Whitebeard, the man who Ace calls father, and his fourteen commanders, the pirates he calls family, who share Luffy’s determination to stop the execution."),
            [(27, 3)] = new EpisodeInfo(27, 3, "Marineford", "Conflict at the Summit", "The players have assembled at last! Whitebeard unleashes a tsunami that is set to swallow every marine and turn Marine HQ upside down. The fight has just begun, as Marine admirals and pirate commanders alike showcase their incredible abilities."),
            [(27, 4)] = new EpisodeInfo(27, 4, "Marineford", "Justice Will Prevail!!", "A larger-than-life ally risks his life to pave the way forward, and the Whitebeard Pirates rush toward Ace. However, just as their rescue mission picks up momentum, Sengoku employs a surprise tactic that turns the tide yet again."),
            [(27, 5)] = new EpisodeInfo(27, 5, "Marineford", "Luffy and Whitebeard", "Luffy leaps into the fray! Battling against familiar faces and new foes alike, he races toward the scaffold like a whirlwind of destruction, and, as his brother begs for him to turn back, Luffy shares some powerful words of his own."),
            [(27, 6)] = new EpisodeInfo(27, 6, "Marineford", "The Prisoners of Impel Down", "Facing Kuma on the battlefield, Ivankov reveals their shared past, but his pleas to his old companion fall on deaf ears as Kuma continues his attacks. In a sea of confusion, only Doflamingo seems to know the truth."),
            [(27, 7)] = new EpisodeInfo(27, 7, "Marineford", "The Whirl Spider", "Luffy’s progress is halted by Mihawk, but the Whitebeard Pirates counter with a swordsman of their own. Then, when an army of human weapons encircle the battlefield, Luffy and his allies find themselves overwhelmed by their explosive attacks."),
            [(27, 8)] = new EpisodeInfo(27, 8, "Marineford", "The Man Who Shook the World", "Squard’s betrayal of Whitebeard sends ripples of distrust and uncertainty throughout the pirate alliance. And yet, Whitebeard’s faith will not be shattered, and it soon becomes clear that his allies are falling into a clever setup launched by the Marines."),
            [(27, 9)] = new EpisodeInfo(27, 9, "Marineford", "Assault", "The pirates are trapped in the bay, held there by Akainu’s magma. Amid the chaos, the order for Ace’s execution is given, and Luffy makes a drastic decision that puts him face-to-face with the Marines' most powerful men."),
            [(27, 10)] = new EpisodeInfo(27, 10, "Marineford", "Oris Plaza", "His adopted son within reach, Whitebeard charges forth, undeterred by the strongest the Marines have to offer. With both sides swinging between victory and defeat, the battlefield plunges into chaos. Then Marco takes to the sky and soars to Ace’s side!"),
            [(27, 11)] = new EpisodeInfo(27, 11, "Marineford", "The White Monster", "Faced with the might of the admirals, even Whitebeard's commanders falter. Refusing to relent, Luffy begs Ivankov for a final chance at victory. Amid a bevy of Marines, an old friend of Luffy’s appears for a much too premature reunion."),
            [(27, 12)] = new EpisodeInfo(27, 12, "Marineford", "The Execution Platform", "Having borne witness to Luffy’s latent powers, the Marines send everything they have at him. But thanks to Ivankov’s quick thinking, Luffy ascends toward Ace. Garp, unable to watch any longer, leaps into Luffy’s path."),
            [(27, 13)] = new EpisodeInfo(27, 13, "Marineford", "The Times They Are A-Changin'", "Ace is free at last, and the pair of brothers are unstoppable! Although the Whitebeard Pirates are emboldened by their teamwork, their captain is not convinced that their escape will come so easily, and Whitebeard makes a difficult decision."),
            [(27, 14)] = new EpisodeInfo(27, 14, "Marineford", "Thank You for Loving Me", "Ace protects his brother from Akainu but suffers a mortal blow in the process. Jinbe and the Whitebeard Pirates rush to his aid but soon realize he is far beyond help. As Ace takes his final breaths, he imparts a heartfelt message."),
            [(27, 15)] = new EpisodeInfo(27, 15, "Marineford", "Edward Newgate", "Blackbeard appears, Thatch’s murder and Ace’s capture all part of a terrifying plan that no one could have imagined! With the war in its final stage, Whitebeard fights to avenge his fallen sons and to punish the traitor who plotted his undoing."),
            [(27, 16)] = new EpisodeInfo(27, 16, "Marineford", "A Gift to a New Era", "Though Ace and Whitebeard have been killed, the Marines seek to wipe out every last pirate, even as they retreat. In the confusion, Akainu sets his eyes on the unconscious Luffy. Meanwhile, Blackbeard reveals his true scheme and a terrifying new power!"),
            [(27, 17)] = new EpisodeInfo(27, 17, "Marineford", "The War's Conclusion", "Senseless violence dyes the battlefield red with blood. Luffy’s allies struggle to save him as the admirals maintain their assault. Suddenly, the Red Hair Pirates appear! Narrowly missing a reunion with Luffy, Shanks utters a speech on the battlefield."),
            [(28, 1)] = new EpisodeInfo(28, 1, "Post-War", "Creeping Future", "The Summit War has thrown the entire world into chaos. Emboldened by Whitebeard's final words, pirates once again envision a wave they can ride into a new era. At Hancock’s order, Luffy is taken to Amazon Lily to hide from the Marines while he recovers."),
            [(28, 2)] = new EpisodeInfo(28, 2, "Post-War", "Gray Terminal", "Before they were brothers, Ace and Luffy lived in the mountains, dreaming of the day when they would take to the sea. In the criminal-infested junkyard known as Gray Terminal, Ace and his partner-in-crime Sabo robbed the thieves and scavengers."),
            [(28, 3)] = new EpisodeInfo(28, 3, "Post-War", "Oath of Brotherhood", "Ace, Sabo, and Luffy made a promise to one another over cups of sake that would shape their futures. Yet while the brothers' adventures continue, Sabo's unfortunate past catches up with him. Meanwhile, a horrific plot unfolds."),
            [(28, 4)] = new EpisodeInfo(28, 4, "Post-War", "Sabo's Sea", "Even as the fires in Gray Terminal threatened to consume them, Ace and Luffy fought Bluejam with all their might! In the wake of the tragedy, Sabo seized freedom in his hands and took to the sea, just as a Celestial Dragon arrived in High Town."),
            [(28, 5)] = new EpisodeInfo(28, 5, "Post-War", "Will of the Winds", "Luffy and Ace buried their sorrow and grew stronger day by day as they trained to be pirates. Time marched on, and they eventually bade farewell to their childhood home and took to the sea, hoping to achieve the dream that Sabo could not."),
            [(28, 6)] = new EpisodeInfo(28, 6, "Post-War", "Cheer", "Determined to return to Sabaody and keep his promise to his crew, Luffy prepares for departure. However, when Dark King Rayleigh appears and makes him a surprising offer, Luffy is forced to make a decision that could alter the fate of his entire journey."),
            [(28, 7)] = new EpisodeInfo(28, 7, "Post-War", "NEWS", "Although Marine HQ is in ruins after the Summit War, Marines and reporters still stalk its grounds. Luffy, Rayleigh, and Jinbe return to where Ace died to carry out a mysterious plan. If all goes well, Luffy’s crew will understand his actions."),
            [(28, 8)] = new EpisodeInfo(28, 8, "Post-War", "3D2Y", "The Straw Hat Pirates have interpreted their captain's message to meet up again in two years. During this time, they will prepare themselves for the New World! In the Calm Belt, Luffy arrives in Luscaina to begin training under Rayleigh."),
            [(29, 1)] = new EpisodeInfo(29, 1, "Return to Sabaody", "Two Years Later", "Two years after the Summit War, the Straw Hat crew finally returns to Sabaody. However, the archipelago has changed, and with dangerous pirates lurking around in droves, their departure to the New World may not go as smoothly as hoped."),
            [(29, 2)] = new EpisodeInfo(29, 2, "Return to Sabaody", "Island of New Beginnings", "Luffy, in disguise, crosses paths with the fake Luffy, and the imposter finds himself on the receiving end of a mysterious power. Meanwhile, Sentomaru and the Marines have yet again amassed a team of Pacifistas."),
            [(29, 3)] = new EpisodeInfo(29, 3, "Return to Sabaody", "Dive, Dive, Dive!", "After two long years and one chaotic afternoon, the Straw Hats are finally ready to renew their voyage. However, before they can set off to Fishman Island, they must contend with a massive Marine force determined to stop them."),
            [(30, 1)] = new EpisodeInfo(30, 1, "Fishman Island", "Into the Depths! The Kraken and the Caribou Pirates", "Encased in a protective bubble, the Sunny departs for Fishman Island far under the sea. As the Straw Hats marvel at the sights, Franky tells the crew about their greatest ally in their time apart. But suddenly, a mysterious shadow closes in on the ship."),
            [(30, 2)] = new EpisodeInfo(30, 2, "Fishman Island", "Adventure in the Ocean Depths! 10 Kilometers Below the Surface!", "Luffy, Zoro, and Sanji leave the relative safety of the Sunny to face the Kraken in its own territory. Putting their powerful new skills to work, they survive the encounter but are swept away by a downward current and become lost in the murky depths."),
            [(30, 3)] = new EpisodeInfo(30, 3, "Fishman Island", "Landing on Fishman Island! The Mermaid Cove and Gyoverly Hills!", "After a near miss with a creepy undersea ship and Fishman Island in sight, Luffy’s crew is halted by a group of fishmen riding atop sea kings. But time is of the essence, so the Straw Hats attempt a desperate maneuver."),
            [(30, 4)] = new EpisodeInfo(30, 4, "Fishman Island", "Arrival at Coral Hill! The Nosebleed and the Devastating Prediction!", "The mermaids of Fishman Island give the Straw Hats a warm welcome, but Sanji is struck with a sudden medical emergency! Luffy and co. take their anemic friend to the Mermaid Cafe to rest, where they are introduced to its soothsaying owner."),
            [(30, 5)] = new EpisodeInfo(30, 5, "Fishman Island", "The King of Fishman Island Invited to Ryugu Palace!", "The pirates receive an unexpected invitation to Ryugu Palace from Neptune himself, king of Fishman Island. Tempted by the smell of food, Luffy wanders the palace and soon finds an enormous tower with a sturdy yet battle-worn door."),
            [(30, 6)] = new EpisodeInfo(30, 6, "Fishman Island", "The Mermaid Princess in Hard-Shell Tower! Shirahoshi of the Neptune Family!", "Plagued by the unwanted advances of a lunatic fishman with a dangerous power, Mermaid Princess Shirahoshi has been hidden away in a lonely tower for years. Meanwhile in the palace, things take a sour turn for the Straw Hats."),
            [(30, 7)] = new EpisodeInfo(30, 7, "Fishman Island", "The Shirahoshi Kidnapping Incident and the Curse of the Mato Mato!", "Ryugu Palace falls into chaos. While Luffy escapes with Shirahoshi, the pirates who infiltrated its walls attack with a strange desperation. After a scuffle of their own, Sanji and Chopper come across an old friend with troubling injuries."),
            [(30, 8)] = new EpisodeInfo(30, 8, "Fishman Island", "The Beginning of the Vengeful Plan! Zoro vs. Hody!", "Hody’s plan for revenge is set in motion, and the Straw Hats who have unwittingly taken control of the palace must now defend it against the New Fishman Pirates. Outside, Luffy’s excursion with Shirahoshi is rudely interrupted."),
            [(30, 9)] = new EpisodeInfo(30, 9, "Fishman Island", "At the Sea Forest! Arlong and Hody", "Jinbe reunites with Luffy in the undersea forest and reveals a shocking connection to an old foe. The former Warlord begins a tale of when two famous figures fought to undo the animosity that existed between humans and fishmen."),
            [(30, 10)] = new EpisodeInfo(30, 10, "Fishman Island", "Otohime, Fisher Tiger, and the Sun Pirates! The Fateful Encounter!", "Queen Otohime tried to lead the Ryugu Kingdom into an era of coexistence between humans and fishmen. At the same time, the heroic Fisher Tiger battled humans to free their slaves from captivity."),
            [(30, 11)] = new EpisodeInfo(30, 11, "Fishman Island", "The Shocking Truth of Tiger! The End of the Sun Pirates", "Fisher Tiger’s past quickly caught up to him, and he left his crew with a sobering, final command. While Jinbe vowed to uphold Tiger’s and Otohime’s ideals, Arlong’s resentment only grew, and soon, the Sun Pirates faced a crisis of identity."),
            [(30, 12)] = new EpisodeInfo(30, 12, "Fishman Island", "The Shipwrecked Celestial Dragon! A Bullet That Seals the Future", "The sudden appearance of a Celestial Dragon almost imperiled Fishman Island, but Queen Otohime’s ability to end hostilities provided a ray of hope for everyone. Just when the path to the future seemed bright, a shot echoed through the plaza."),
            [(30, 13)] = new EpisodeInfo(30, 13, "Fishman Island", "Shaking of the Kingdom! Luffy vs. Jinbe!", "After completing his tale, Jinbe begs Nami for forgiveness. Soon after, Hody broadcasts across all of Fishman Island, and all watch in horror as he conducts his hostile takeover. Luffy is ready to fight, but Jinbe has something else in mind."),
            [(30, 14)] = new EpisodeInfo(30, 14, "Fishman Island", "Gyoncorde Plaza Gathering! The Evil Drug's True Power", "Hody experiences a strange side effect of the energy steroids, and his body undergoes a horrifying transformation! The three princes of the Ryugu Kingdom rush to the scene, but Hody's crewmates have a dirty trick up their sleeves."),
            [(30, 15)] = new EpisodeInfo(30, 15, "Fishman Island", "Hody's Dark Secret! The Straw Hats' Arrival", "Shirahoshi and Jinbe hurry to free King Neptune but run headlong into a trap. There, Hody reveals a shocking truth about the history of his revolution, but even in the face of Hody’s cruelty, Shirahoshi holds true to her mother’s dying wish."),
            [(30, 16)] = new EpisodeInfo(30, 16, "Fishman Island", "100,000 vs. 10! The Straw Hats' Explosive Moves", "The Straw Hats have reassembled at last, an army of New Fishman Pirates standing before them. With the last two years still fresh in their minds, the Straw Hats’ training is put to the test, and they unleash a flurry of new techniques."),
            [(30, 17)] = new EpisodeInfo(30, 17, "Fishman Island", "The General from the Future Land and the Crisis of Fishman Island's Destruction", "Franky’s mechanical masterpieces have transformations in store that can only be described as \"super!\" However, onlookers curb their enthusiasm when a foreboding shadow is cast upon the plaza by a giant ship hurtling toward the island!"),
            [(30, 18)] = new EpisodeInfo(30, 18, "Fishman Island", "I'll Protect Everything", "Shirahoshi flees into the open sea, hoping to draw the Noah away from Fishman Island. Luffy follows her, but he must contend with Hody Jones along the way. Luckily, the appearance of a new ally gives Luffy a chance to protect Shirahoshi."),
            [(30, 19)] = new EpisodeInfo(30, 19, "Fishman Island", "What Are You?", "The Straw Hats hold their own against the powerful officers from Hody’s crew. But their struggle will be in vain if Luffy cannot stop the Noah. Meanwhile, Hody becomes stronger than ever."),
            [(30, 20)] = new EpisodeInfo(30, 20, "Fishman Island", "Phantom", "Neptune’s army aids the Straw Hats against the New Fishman Pirates, finally accepting the crew as allies. As the fight continues, Fukaboshi’s transponder snail reveals the bone chilling truth behind Hody's motivations."),
            [(30, 21)] = new EpisodeInfo(30, 21, "Fishman Island", "Death Is Also Revenge", "Luffy has a fist of fire with Hody’s name on it, and the rest of the Straw Hats push forward with all their might. Hody takes even more energy steroids, and a gravely injured Luffy struggles to overcome the fishman's unbelievable stamina."),
            [(30, 22)] = new EpisodeInfo(30, 22, "Fishman Island", "The Path Toward the Sun", "Hody is vanquished, the Noah is stopped, and Fishman Island is saved! Unfortunately, Luffy was gravely injured in the process. With no time to spare, a true friend steps forward to tear down the walls of the island's history."),
            [(30, 23)] = new EpisodeInfo(30, 23, "Fishman Island", "The Voice from the New World", "When pirates working for Big Mom appear, Fishman Island falls under threat from an adversary even stronger than Hody. Luffy steps up to save the fishmen once again but may doom himself by antagonizing one of the strongest pirates in the New World."),
            [(30, 24)] = new EpisodeInfo(30, 24, "Fishman Island", "The Hero's Hat", "Filled with both excitement and trepidation, the crew finally sets sail for the New World. After bidding their fond farewells, the Straw Hats leave with a new log pose in hand. Overcoming trials and tragedies, Luffy's dream is on course once again!"),
            [(31, 1)] = new EpisodeInfo(31, 1, "Punk Hazard", "Punk Hazard", "Fresh off the adventure at Fishman Island, the Sunny is caught in a massive whirlpool and dragged off course. A desperate cry for help leads the Straw Hats to Punk Hazard, an island engulfed in flames and inhabited by a mythic beast."),
            [(31, 2)] = new EpisodeInfo(31, 2, "Punk Hazard", "Adventure on the Burning Island", "Luffy, Robin, Usopp, and Zoro face a powerful dragon, and Luffy gets up close and personal with a torso-less figure. Elsewhere, Nami, Sanji, Chopper, and Franky find themselves imprisoned with the pieces of a human face!"),
            [(31, 3)] = new EpisodeInfo(31, 3, "Punk Hazard", "About My Torso", "Luffy’s group finds themselves up against a gang of centaurs, Brook battles an aggressive mysterious torso, and Vice Admiral Smoker encounters someone highly unexpected! Meanwhile, Nami and Chopper seek a safe exit for a group of children."),
            [(31, 4)] = new EpisodeInfo(31, 4, "Punk Hazard", "The Lake of Bandits", "Luffy’s group takes a deadly plunge into icy waters, and Trafalgar Law uses his unique abilities to rearrange Nami’s group and place the Straw Hats into a state of chaos! Then, things heat up when G-5 squares off with Law."),
            [(31, 5)] = new EpisodeInfo(31, 5, "Punk Hazard", "CC", "Luffy is excited to once again cross paths with Trafalgar Law. While Vice Admiral Smoker determines his next move, an exceptionally large pirate with alligator legs tells Luffy and the Straw Hats the true story of Punk Hazard’s mysterious transformation!"),
            [(31, 6)] = new EpisodeInfo(31, 6, "Punk Hazard", "Yeti Cool Brothers", "The giant children fly into a mysterious rage, and Chopper discovers the true reason for their unusual condition! Meanwhile, Zoro, Brook, and Sanji cross paths with a dangerous duo, and the story of how Trafalgar Law arrived at Punk Hazard is revealed."),
            [(31, 7)] = new EpisodeInfo(31, 7, "Punk Hazard", "Pirate Alliance", "Luffy agrees to an unexpected alliance with Trafalgar Law, but the Straw Hats refuse to leave the island until the children are safe. Meanwhile, the gaseous master of the island unleashes something extremely deadly!"),
            [(31, 8)] = new EpisodeInfo(31, 8, "Punk Hazard", "Blizzard with a Chance of Slime", "Zoro, Sanji, and Brook continue their search for the samurai, and Law tries to sneak Chopper into the laboratory! And then, Luffy’s battle with his old nemesis Smoker is interrupted by the arrival of a terrifying toxic blob."),
            [(31, 9)] = new EpisodeInfo(31, 9, "Punk Hazard", "My Name Is Kin'emon!!", "Law's plan to infiltrate Caesar’s facility hits a snag when an unexpected guest complicates things. Meanwhile, Chopper investigates the Master’s lab, and the mysterious samurai shows what he can do with a full body!"),
            [(31, 10)] = new EpisodeInfo(31, 10, "Punk Hazard", "Vergo and Joker! The Beginning of Caesar's Masterplan!", "Caesar gathers his young test subjects, shocking secret identities are revealed, and the poisonous purple blob continues its march across the island! Meanwhile, Nami and Usopp get an unexpected helping hand in their fight to save the children."),
            [(31, 11)] = new EpisodeInfo(31, 11, "Punk Hazard", "The Experiment Begins! Shinokuni Unleashed!", "A candy addiction leads the children to return, and Caesar plans to show the world his new and improved Smiley! Luffy and the Straw Hats next find themselves pursued by a deadly cloud of gas that turns living things into statues of ash!"),
            [(31, 12)] = new EpisodeInfo(31, 12, "Punk Hazard", "The Counterattack Begins!", "Law has a trick up his sleeve that turns the tide in the alliance’s favor, to include an on-the-spot partnership with former foes. Zoro and company reunite with Nami and Sanji, and it is all Luffy’s group can do to ensure everyone’s safety in the lab."),
            [(31, 13)] = new EpisodeInfo(31, 13, "Punk Hazard", "G-5's Sacrifice and the Betrayal of the Vice Admiral!", "Caesar hatches a plan to forever silence his enemies, and Tashigi’s men make the ultimate sacrifice! Luffy races toward Caesar to deliver on his promise to beat and capture him, and Chopper does all he can to keep the children from consuming more candy."),
            [(31, 14)] = new EpisodeInfo(31, 14, "Punk Hazard", "The Mastermind in the Shadows and the Battle Between Gas and Rubber!", "Law finds the room that could ruin the New World, and Luffy hopes to bring Caesar’s reign of terror to a brutal end! With Law's true ambitions revealed, Doflamingo flies into action to protect his criminal operation!"),
            [(31, 15)] = new EpisodeInfo(31, 15, "Punk Hazard", "Vice Admiral vs. Vice Admiral and the Truth Behind Punk Hazard's Destruction!", "Smoker attacks the estranged Vice Admiral Vergo for assaulting his men, and in the trash heap, Luffy meets something strange. Meanwhile, Caesar’s bloated words to his men about the island’s history do not match what occurred."),
            [(31, 16)] = new EpisodeInfo(31, 16, "Punk Hazard", "The Dragon Named Momonosuke and the Menacing Snow Fruit User!", "Luffy befriends a dragon that used to be a human boy, and Vice Admiral Smoker continues his brutal battle with the villainous Vergo. Mocha tries to keep the Master’s candy away from her friends, and Zoro pits his steel against snow in a battle with Monet."),
            [(31, 17)] = new EpisodeInfo(31, 17, "Punk Hazard", "Zoro's Strongest One-Sword Style and Mocha's Plight!", "Zoro vs. Monet: a duel to the bitter end. But when Tashigi comes to lend a hand, she will need her full might to compensate for Zoro's weakness. Meanwhile, Mocha is running out of options for keeping the candy away from the children."),
            [(31, 18)] = new EpisodeInfo(31, 18, "Punk Hazard", "Luffy and Law's All-out Attack! The Takeover of the New Era!", "Brownbeard heroically attempts to expose Caesar as a villain, and the horrific truth about Punk Hazard is finally revealed! Luffy makes Caesar pay for his crimes against the children, and Smoker risks his life for a chance to see Vergo fall in battle!"),
            [(31, 19)] = new EpisodeInfo(31, 19, "Punk Hazard", "The Strongest Grizzly Magnum and the Assassins from Dressrosa!", "Law concludes his epic battle with Vergo, and Luffy looks to land a devastating blow on the murderous gasbag mastermind. Meanwhile, word of Luffy and Law’s alliance spreads throughout the world, and Joker sends a team to rescue Caesar."),
            [(31, 20)] = new EpisodeInfo(31, 20, "Punk Hazard", "Die for Me! The Dangerous Man Makes His Move!", "As the Straw Hats and co. flee the crumbling laboratory, Caesar’s desperate act of vengeance thwarts Joker’s plan to forever silence his enemies. Franky continues his battle with Buffalo and Baby 5, and Usopp takes a shot at capturing Caesar."),
            [(31, 21)] = new EpisodeInfo(31, 21, "Punk Hazard", "The Alliance's Target and the Departure!", "Bitter enemies set aside their differences to enjoy a well-deserved feast. The weary Marines and the rescued children get teary-eyed as they say goodbye to the Straw Hats. Meanwhile, Law enters into treacherous negotiations with Joker."),
            [(31, 22)] = new EpisodeInfo(31, 22, "Punk Hazard", "The Arrival of the Mysterious Man and the News Coo!", "Doflamingo—a.k.a. Joker—arrives on the scene and wreaks havoc! Only an icy intercession can save Smoker and send Joker off to rethink his strategy. Meanwhile, the Sunny sails through the rough seas of the New World where even a bit of hail portends doom!"),
            [(32, 1)] = new EpisodeInfo(32, 1, "Dressrosa", "Arriving at Dressrosa! The Country of Passion, Love, and Toys!", "Dressrosa! The Straw Hats split up and set about their business on this passionate, enchanting island! But here, in the heart of enemy territory, danger lurks around every corner. Luffy’s group encounters an intriguing blind man whose luck has run dry."),
            [(32, 2)] = new EpisodeInfo(32, 2, "Dressrosa", "Corrida Colosseum! The Great Tournament", "Zoro and co. strike off in search of the thieving fairy who has taken Zoro's sword, but Sanji gets distracted by the alluring dancing of a gorgeous Dressrosan. Meanwhile, the prize of the late Ace’s devil fruit draws Luffy and Franky to the colosseum."),
            [(32, 3)] = new EpisodeInfo(32, 3, "Dressrosa", "The Colosseum of Many New Faces and Maynard the Pursuer!", "Luffy enters the tournament at Corrida Colosseum to compete for Ace's fruit, but with the mightiest warriors of the New World assembled and standing in his way, the competition will not be easy. The first stage of the competition is a battle royal!"),
            [(32, 4)] = new EpisodeInfo(32, 4, "Dressrosa", "Bellamy's Changed Motivations and B Block's Start!", "When Block A concludes, the crowds are shocked to discover the secret identity of the winner. Block B commences, and the widely despised Bartolomeo takes the ring, but he will have to contend with Dressrosa's own local favorite, Bellamy!"),
            [(32, 5)] = new EpisodeInfo(32, 5, "Dressrosa", "B Block on Fire! Outlandish Competitors and King Punch!", "The battle in Block B hits a crescendo as the Fighting King Elizabello II prepares his devastating King Punch! But as the powerful attack becomes imminent, the Kingdom of Prodence faction unravels!"),
            [(32, 6)] = new EpisodeInfo(32, 6, "Dressrosa", "To Green Bit! The Adventure in the Land of Dwarves!", "The Caesar Transfer Team is on approach to Green Bit, but the colossal coastal fish of Dressrosa are biting, and the connecting bridge has collapsed! The team gathers their wits to make it across, but they soon discover they are not alone on the island."),
            [(32, 7)] = new EpisodeInfo(32, 7, "Dressrosa", "Doflamingo Arrives! The Ultimate Schemer!", "All but done for, Sanji is trapped by Violet and her crew when she suddenly changes her mind and mysteriously turns on her own men! Sanji gets in touch with Law to share a shocking secret that could unsettle all their plans in Dressrosa."),
            [(32, 8)] = new EpisodeInfo(32, 8, "Dressrosa", "Closely Contested Block C! Lucy and Ucy!", "The Sunny is mysteriously deformed by Giolla’s strange powers! Meanwhile, the battle for Block C is under way, but Luffy suffers a staggering setback. He eventually knocks down the giant Hajrudin, but Chinjao has his sights set on Garp’s grandson!"),
            [(32, 9)] = new EpisodeInfo(32, 9, "Dressrosa", "C Block's Blazing Battle and the Forgotten of Dressrosa!", "In the colosseum, Chinjao finally faces Luffy and reveals the old grudge he bears against Garp! Meanwhile, Usopp and Robin get swept up in the Tontattas' raid against the royal family, and Franky learns the eerie secret of Dressrosa’s living toys."),
            [(32, 10)] = new EpisodeInfo(32, 10, "Dressrosa", "The Tontatta Rebellion and the C Block Finale!", "The Tontattas gear up for their all-out assault on the Doflamingo Family, and Usopp and Robin are caught up in the war effort! Meanwhile, Luffy concludes his earth-shattering fight to the death with Don Chinjao."),
            [(32, 11)] = new EpisodeInfo(32, 11, "Dressrosa", "A Sad Past! Rebecca and Soldier-san", "A voice from the past unsettles Luffy and sends him into a rage! Also, it's discovered that Rebecca's eternal bond with the one-legged toy soldier is linked to the violent overthrow of the old regime, perpetrated by none other than Doflamingo!"),
            [(32, 12)] = new EpisodeInfo(32, 12, "Dressrosa", "D Block's Fiery Start and the Heavenly Demon's Revelation!", "As Rebecca enters the ring for the battle for Block D, the crowd shows its contempt for her heritage and vicious contenders set their sights on her. Meanwhile, Law is pinned by Fujitora, and Doflamingo has the upper hand."),
            [(32, 13)] = new EpisodeInfo(32, 13, "Dressrosa", "Law's New Strategy and the Colosseum's Underground Nightmare!", "Law must put himself in harm's way to defend the Sunny and the Straw Hats from Doflamingo's all-out assault. Meanwhile, Bartolomeo searches the colosseum for Luffy and discovers something strange about the medical center for the defeated."),
            [(32, 14)] = new EpisodeInfo(32, 14, "Dressrosa", "The Plight of the Riku Clan! The Lurking Hero Awaits in the Shadows!", "The losers of the colosseum who are being held below the infirmary have an unexpected addition to their numbers. Meanwhile, the history of Tontatta is made clear, as is the shocking identity of the toy soldier!"),
            [(32, 15)] = new EpisodeInfo(32, 15, "Dressrosa", "Doflamingo's Relentless Pursuit", "Revealed is the sad fate of King Riku, his army, and his daughter, Princess Viola, as well as how the people of Dressrosa fell into the hands of Doflamingo. Not to mention, all the toys in the country share a mysterious secret."),
            [(32, 16)] = new EpisodeInfo(32, 16, "Dressrosa", "SOP Strategy", "Zoro and Kin'emon make a mad dash to save Law, but Admiral Fujitora stands in their way, bringing his full power against them. The toy soldier lays out the plan to liberate the Tontattas and bring down the Doflamingo Family: Operation SOP."),
            [(32, 17)] = new EpisodeInfo(32, 17, "Dressrosa", "The Underground World", "Usopp and Robin travel via an underground tunnel dug by the tiny people of the Tontatta Tribe. On the surface, Franky makes his way to the Toys House to cause a distraction to help his crewmates, but standing in his way is quite the guy!"),
            [(32, 18)] = new EpisodeInfo(32, 18, "Dressrosa", "Fujitora's Outlook", "Viola, once a princess of the Dressrosan royal family, shows Luffy and the others a secret entrance into the palace. In the meantime, Admiral Fujitora reveals his audacious plans for the Seven Warlords of the Sea!"),
            [(32, 19)] = new EpisodeInfo(32, 19, "Dressrosa", "Diamante and Pica", "The final round at Corrida Colosseum kicks off, and Diamante joins the combatants in an all-out struggle to win the prize! Meanwhile, Luffy and co., en route to Doflamingo, are faced with an immovable member of the Donquixote Family."),
            [(32, 20)] = new EpisodeInfo(32, 20, "Dressrosa", "Captain", "With Operation SOP in its final stage, the Tontattas are devastated by Trebol’s furious attack, but as Usopp tries to convince Leo to abort the operation, Leo tells him about their commander’s stunning true identity."),
            [(32, 21)] = new EpisodeInfo(32, 21, "Dressrosa", "Usoland the Liar", "With the Tontattas beaten, Robin turned into a toy, and Luffy, Franky, and the others busy elsewhere, Usopp is the last hope for Operation SOP! Among the cries of the Tontattas, Usopp has a difficult decision to make."),
            [(32, 22)] = new EpisodeInfo(32, 22, "Dressrosa", "Dressrosa in Turmoil", "Usopp creates a miracle! Toys across Dressrosa regain their true forms, a flood of lost memories return, and a forgotten hero launches a long overdue attack on the villainous tyrant Doflamingo!"),
            [(32, 23)] = new EpisodeInfo(32, 23, "Dressrosa", "Bird Cage", "Sabo makes a risky move to end the championship battle, and Luffy hurries to free Law. Doflamingo's apparent defeat has thrown Dressrosa into chaos, but he may have a trick or two left up his sleeve."),
            [(32, 24)] = new EpisodeInfo(32, 24, "Dressrosa", "Executive Officer Pica", "Doflamingo announces the rules for his deadly game with the highest bounty reserved for… Usopp! As Luffy and co. head toward the palace, Fujitora stands in their way, and Pica emerges in town to change the stakes of the game."),
            [(32, 25)] = new EpisodeInfo(32, 25, "Dressrosa", "Onward!! Army of Rogues", "The game is afoot, and Luffy finds that the dastardly Warlord has more enemies than he might realize! Strangely, an argument breaks out to decide who gets the honor of taking down Doflamingo once and for all!"),
            [(32, 26)] = new EpisodeInfo(32, 26, "Dressrosa", "The State of the War", "Law's reason for despising Doflamingo is revealed! Luffy heads toward Doflamingo's refuge, making his way through the guards, while down below Admiral Fujitora faces off against Sabo, who is now armed with a familiar fiery power!"),
            [(32, 27)] = new EpisodeInfo(32, 27, "Dressrosa", "Battle", "Heavy hitters from the colosseum turn from wringing each other's necks toward a unified plan, which might be just enough to give Luffy the chance he needs to make it to a final showdown with Doflamingo."),
            [(32, 28)] = new EpisodeInfo(32, 28, "Dressrosa", "A Man's World", "Cavendish shares his fears that the \"Birdcage\" game Doflamingo is playing has been rigged all along! Meanwhile, the Tontattas are in full revolt, Zoro finally gains the upper hand against Pica, and Franky finds a way to deal with Senor Pink."),
            [(32, 29)] = new EpisodeInfo(32, 29, "Dressrosa", "Trump Card", "Gladius' exploding spheres send Robin and Bartolomeo hurtling directly into Luffy and co.’s battle with some creepy dolls. Rebecca makes it to the Flower Field alone, and Sabo tastes Fujitora's full might!"),
            [(32, 30)] = new EpisodeInfo(32, 30, "Dressrosa", "Secret Plan", "Luffy and Law finally surmount incredible odds to land themselves in striking distance of Doflamingo. However, Sugar stands in their way, so Usopp uses Viola's clairvoyance to prepare his ammunition to take her out."),
            [(32, 31)] = new EpisodeInfo(32, 31, "Dressrosa", "The Operation Fruit", "All hopes rest on Luffy and Law being able to take down Doflamingo; however, the Warlord has other plans in mind as he gives the two the fight of their lives, and he reveals his true, shocking history! Meanwhile, Sabo finds out Fujitora’s true intention."),
            [(32, 32)] = new EpisodeInfo(32, 32, "Dressrosa", "White Town", "A rage born from the lies of the World Government created a child who had the will to try and kill Corazón! Law was accepted into the Doflamingo Family, but Corazón's mysterious silence hid a fact that just may have saved Law's life."),
            [(32, 33)] = new EpisodeInfo(32, 33, "Dressrosa", "The Island of Fate: Minion", "Things grew stranger for Law when he learned the secret behind why Corazón feigned being mute. But the mysterious Corazón’s actions were only beginning to baffle Law when he took him on a journey to cure the youth’s disease."),
            [(32, 34)] = new EpisodeInfo(32, 34, "Dressrosa", "Cora", "Corazón had finally obtained the Op Op Fruit! Doflamingo and his Family were zeroing in on Corazón and Law's whereabouts, so Corazón hid Law long enough to let him escape, even at the risk of his own life."),
            [(32, 35)] = new EpisodeInfo(32, 35, "Dressrosa", "Bellamy the Pirate", "Luffy continues to fight the Doflamingo doppelganger but keeps accidentally attacking Bellamy instead. Meanwhile, the tide of battle turns against Law as painful scars from the past resurface, and Zoro gets unexpected help in his battle with Pica."),
            [(32, 36)] = new EpisodeInfo(32, 36, "Dressrosa", "The Spear of Elbaph and Don Sai", "Hajrudin has been beaten down, but a fire still burns within him to pay back the favor he owes! Lao G and Don Chinjao bring their most devastating attacks to bear, but a misunderstanding caused by Baby 5 has Sai and Don Chinjao distracted."),
            [(32, 37)] = new EpisodeInfo(32, 37, "Dressrosa", "Cabbage and Lomeo", "A mysterious force that resembles Cavendish menaces the battlefield! While it appears to help Bartolomeo and co. at first, it soon sets its sights on Robin! As he continues to battle Gladius, Bartolomeo is faced with an impossible dilemma."),
            [(32, 38)] = new EpisodeInfo(32, 38, "Dressrosa", "To Russian, with Love", "Senor Pink and Franky continue their knock-down, drag-out, manly fight. As both are pushed to their limits, Senor Pink's tragic past is revealed! Meanwhile, Princess Mansherry's rescue hits a snag as she stubbornly refuses to leave!"),
            [(32, 39)] = new EpisodeInfo(32, 39, "Dressrosa", "Zoro vs. Pica", "Pica loses his temper and becomes a terrible and true force of nature. With the lives of King Riku, Usopp, and the others in the balance, Zoro must dig deep and discover a powerful technique to counter Pica's near-unstoppable onslaught."),
            [(32, 40)] = new EpisodeInfo(32, 40, "Dressrosa", "The Curse of Heart", "The Tontattas finish demolishing the SMILE Factory and taking down Bellamy only enrages Luffy in his fight against Doflamingo, his anger multiplying when he finds Law's lifeless body! Meanwhile, the birdcage draws ever tighter around Dressrosa."),
            [(32, 41)] = new EpisodeInfo(32, 41, "Dressrosa", "In My Way", "Law summons enough energy to speak, denouncing the Doflamingo Family executives as pawns in Doflamingo's game. Meanwhile, neither Gear Second nor Gear Third attacks are working against Doflamingo, so Luffy decides to use something stronger!"),
            [(32, 42)] = new EpisodeInfo(32, 42, "Dressrosa", "Gear Fourth", "The birdcage tightens its grip on Dressrosa, so Zoro sets off with a plan to stop it from slicing apart the country. Meanwhile, Luffy digs deep into his training to provide an answer born of rubber and haki to combat Doflamingo’s might."),
            [(32, 43)] = new EpisodeInfo(32, 43, "Dressrosa", "Gatz", "Using Boundman drains Luffy of his haki, leaving him in need of a rest. Luckily, the colosseum combatants run interference while he recharges, and Sabo steps in at a crucial moment to defend his brother."),
            [(32, 44)] = new EpisodeInfo(32, 44, "Dressrosa", "Heaven and Earth", "With just over a minute to spare, everyone in Dressrosa turns their efforts toward slowing the birdcage. Meanwhile, Rebecca, under the control of Doflamingo, advances to attack Viola, and a hero’s recovery cannot come soon enough!"),
            [(32, 45)] = new EpisodeInfo(32, 45, "Dressrosa", "Prostration", "The people of Dressrosa shed tears of joy as they remember the hardships endured and the actions taken to free them. As they beg King Riku to resume the crown, Admiral Fujitora shocks onlookers with an unexpected display of humility."),
            [(32, 46)] = new EpisodeInfo(32, 46, "Dressrosa", "Sabo's Adventure", "The combatants in the battle for Dressrosa can finally begin to recoup, but those who stay awake are treated to the story of how Sabo joined the Revolutionary Army and how he reunited with his brother Luffy at the colosseum."),
            [(32, 47)] = new EpisodeInfo(32, 47, "Dressrosa", "Soldier's Conviction", "Major players in the Marines arrive to search Dressrosa for the Straw Hats, and Rebecca's history is whitewashed with a cover story. Luffy and the others make a mad dash for the east port, but not before finishing up a piece of business."),
            [(32, 48)] = new EpisodeInfo(32, 48, "Dressrosa", "Father and Sons", "Law and Sengoku share memories of Corazón and the secret of Trafalgar Law's name. Luffy and the others finally make their break from the island; however, an unbelievable new chapter opens in the story of the Straw Hat Pirates!"),
            [(33, 1)] = new EpisodeInfo(33, 1, "Zou", "Zou", "The mysterious island of Zou approaches in the distance, obscured by fog. When the haze clears, what greets the Straw Hats is beyond anyone’s expectations! Luffy and co. find the Sunny nearby, but the rest of the crew has already disembarked."),
            [(33, 2)] = new EpisodeInfo(33, 2, "Zou", "The Minks", "The mysterious \"island\" may hold more danger than was bargained for, as Luffy and co. learn they are not alone! Luffy pushes deeper into the mysterious forest, but he is treading territory where he is not welcome."),
            [(33, 3)] = new EpisodeInfo(33, 3, "Zou", "At Rightflank Fortress", "Luffy and co. ride out the rain-ruption and learn of its vital role in maintaining life on Zou. As they continue toward the fortress, they learn more about the mysterious minks, and are finally reunited with the others."),
            [(33, 4)] = new EpisodeInfo(33, 4, "Zou", "Duke Inuarashi and Master Nekomamushi", "The Straw Hats finally meet the daytime ruler of this strange land. They learn more about the battle that took place here and discover the mysterious system by which the island is ruled."),
            [(33, 5)] = new EpisodeInfo(33, 5, "Zou", "The Curly Hat Pirates Arrive", "While they put up a good fight, the minks were not able to subdue Jack. In the aftermath of Jack's deadly attack, Chopper and Sanji rushed in to save the fallen minks and enlisted the help of Caesar to overcome the lingering gas problem."),
            [(33, 6)] = new EpisodeInfo(33, 6, "Zou", "Tea Party Invitation", "The mystery of Sanji's disappearance only deepens as Luffy learns that Big Mom’s lackeys caught up with him and offered him an invitation from one of the Four Emperors that simply could not be refused."),
            [(33, 7)] = new EpisodeInfo(33, 7, "Zou", "Let's Go See Master Nekomamushi", "Being separated from one of his crew is more than Luffy is willing to tolerate! The pirates make their way toward the nighttime ruler to find out more about their friend's disappearance."),
            [(33, 8)] = new EpisodeInfo(33, 8, "Zou", "Raizo of the Mist", "Revelations abound as the true nature of the men from Wano and their relationship with the minks is revealed, and Momonosuke's role is greater than any had imagined. But even more shocking is the visage of a real ninja!"),
            [(33, 9)] = new EpisodeInfo(33, 9, "Zou", "Momonosuke, Heir of the Kozuki Family", "More mysteries of the Kozuki Clan are revealed, and its retainers propose an alliance to help topple one of the Four Emperors: Kaido of the Beast Pirates! But first, Luffy has some important Straw Hat business to attend to."),
            [(33, 10)] = new EpisodeInfo(33, 10, "Zou", "Understood", "The Straw Hats divide and conquer: one group to go save Sanji, and the other to Wano! But just before their departure, the tremendous elephant quakes as Jack’s fleet launches an aggressive attack on the monstrous Zunesha."),
            [(34, 1)] = new EpisodeInfo(34, 1, "Whole Cake Island", "A World Abuzz", "Luffy's team—plus a stowaway—sets out from Zou on their way to win back Sanji. Meanwhile, the leading families and leaders of the world make their way to the Holy Land, Mary Geoise, for a pivotal round table conference."),
            [(34, 2)] = new EpisodeInfo(34, 2, "Whole Cake Island", "0 and 4", "Without Sanji to properly identify safe and edible food, Luffy gets a stomach full of poisonous fish! With no antidotes on board, the Straw Hats must rely on a couple of mysterious—yet somehow familiar—passersby for help."),
            [(34, 3)] = new EpisodeInfo(34, 3, "Whole Cake Island", "Totto Land", "Arriving on Cacao Island, Luffy and Chopper are in heaven as everything before their eyes is edible. There, the Straw Hats encounter Sanji’s betrothed, Pudding, but when they challenge her for Sanji's heart, her answer is truly shocking."),
            [(34, 4)] = new EpisodeInfo(34, 4, "Whole Cake Island", "Pirate Emperor, Charlotte Linlin", "Big Mom ravages Whole Cake Island in search of sweets, and while a familiar face across the waters has just what she needs, he also brings ill tidings. As the Straw Hats draw closer to the island, Pedro shares his sordid past in Big Mom’s territory."),
            [(34, 5)] = new EpisodeInfo(34, 5, "Whole Cake Island", "Adventure in the Mysterious Forest", "Luffy’s group makes its way toward Sanji, only to be distracted by the endless supply of sweets and a second Luffy. Before they realize it, they are lost in the Seducing Woods! Meanwhile, Sanji confronts his older brother."),
            [(34, 6)] = new EpisodeInfo(34, 6, "Whole Cake Island", "Vinsmoke Judge", "Sanji faces his father in a duel as the wounds of his childhood are laid bare! Meanwhile, Luffy falls for the illusions of the forest, and Big Mom has Caesar at her whims, ready to use him to continue her maniacal pursuits."),
            [(34, 7)] = new EpisodeInfo(34, 7, "Whole Cake Island", "Lola's Vivre Card", "The mysterious half-buried man comes clean about his identity, but this must all be put aside when one of Big Mom's most powerful commanders comes calling. Nami's past exploits, however, hold the key to keeping the baddies at bay."),
            [(34, 8)] = new EpisodeInfo(34, 8, "Whole Cake Island", "Luffy vs. Commander Cracker", "Luffy squares off against a commander of Big Mom, Charlotte Cracker, whose powers may prove more troublesome than they appear. Meanwhile, Brook and Pedro infiltrate Whole Cake Island and work out a plan to avoid getting caught."),
            [(34, 9)] = new EpisodeInfo(34, 9, "Whole Cake Island", "The Iron Mask", "Sanji challenges his entire family, but they have effectively ensured he follows their wedding scheme. The truth of Germa's power and Vinsmoke Judge’s dark history is also revealed. Meanwhile, Nami has recruited the homies and challenges Cracker!"),
            [(34, 10)] = new EpisodeInfo(34, 10, "Whole Cake Island", "To the East Blue", "Sanji’s tragic history included a rare moment of compassion that was his only chance at escape, but even then, his father’s cruelty haunted him. In the present, Reiju helps Sanji straighten himself out before he meets with his fiancée."),
            [(34, 11)] = new EpisodeInfo(34, 11, "Whole Cake Island", "Luffy vs. Sanji", "Luffy finishes off Cracker and finally catches up with Sanji, but the Straw Hats’ cook seems far from thrilled about the reunion. A heartbreaking duel commences, but a steadfast Luffy makes a solemn vow to his friend and crewmate."),
            [(34, 12)] = new EpisodeInfo(34, 12, "Whole Cake Island", "Forces of Rage", "Luffy stands his ground and waits for Sanji to return, but as the vengeful army washes over him, only a true friend can step in to help. At Big Mom’s castle, Sanji and Pudding discuss their marriage and come to a startling conclusion!"),
            [(34, 13)] = new EpisodeInfo(34, 13, "Whole Cake Island", "Luffy and Big Mom", "Pedro creates a diversion for Brook to put his soulful plan into action. Meanwhile, the captured Luffy continues to goad Big Mom, and Pudding has the beginnings of rebellion stirring up inside her."),
            [(34, 14)] = new EpisodeInfo(34, 14, "Whole Cake Island", "A Ray of Light", "When Pudding arrives to meet the captured Luffy and Nami, their hopefulness suddenly turns to shock! To make matters worse, Brook comes face to face with Big Mom herself! At the risk of his own well-being, Luffy desperately tries to free himself."),
            [(34, 15)] = new EpisodeInfo(34, 15, "Whole Cake Island", "Germa's Failure", "Things are looking grim for Luffy and Nami until an unexpected ally appears. Elsewhere, Sanji confronts his sister about what Pudding plans to do and learns the powerful secret of their mother's undying love."),
            [(34, 16)] = new EpisodeInfo(34, 16, "Whole Cake Island", "Not Here", "Luffy fights through more obstacles in search of Sanji, and Brook hears Big Mom's plan firsthand as she shares her intentions with Pudding. While Big Mom's agents believe most of the Straw Hats to be under their control, Mont d'Or has his doubts."),
            [(34, 17)] = new EpisodeInfo(34, 17, "Whole Cake Island", "Liar", "Nami and co. finally locate Brook using the Mirro-world, but he is in the most inopportune of places! Meanwhile, Luffy is waylaid on his way back to the rendezvous, and Jinbe shares the awful truth about the wedding with the other Straw Hat pirates."),
            [(34, 18)] = new EpisodeInfo(34, 18, "Whole Cake Island", "Rook", "Jinbe tells the Straw Hats about Capone “Gang” Bege and proposes a shocking plan to take down Big Mom! But before they are granted an audience with Bege, they must make themselves presentable."),
            [(34, 19)] = new EpisodeInfo(34, 19, "Whole Cake Island", "Emperor Assassination Plan", "The plan for Big Mom’s assassination is all set. Sanji heads back to his room to avoid suspicion, but Mont d’Or is hot on his trail. The day arrives, and guests from the criminal underground pour in in droves, some with their own agendas to settle."),
            [(34, 20)] = new EpisodeInfo(34, 20, "Whole Cake Island", "Veteran Actor", "Final preparations abound, plans come together, and Sanji meets his bride on the altar. As the veil is lifted off Pudding's face, emotions run high, and the chaos begins! As the proverbial icing on the cake, all hell breaks loose as Luffy charges in!"),
            [(34, 21)] = new EpisodeInfo(34, 21, "Whole Cake Island", "The Honorable", "The wedding and the precious cake crumble to pieces around Big Mom as a slew of Luffys wreak havoc. The chaos pushes Big Mom past the edge of sanity, so her children make a move against Germa while Pudding takes aim at Sanji."),
            [(34, 22)] = new EpisodeInfo(34, 22, "Whole Cake Island", "Hey, Mother", "Luffy dashes toward Big Mom to show her the broken portrait again, and Sanji attempts to free his family. Here, Big Mom’s tragic and sad history is revealed: from the very beginning, Big Mom's voracious hunger has brought her into conflict with others."),
            [(34, 23)] = new EpisodeInfo(34, 23, "Whole Cake Island", "HAPPY BIRTHDAY", "A young Linlin managed to enrage the giants of Elbaph, but Mother Carmel begged for clemency on her behalf. After Carmel’s mysterious disappearance, Linlin fell in with a new cohort and found her reason to go on living."),
            [(34, 24)] = new EpisodeInfo(34, 24, "Whole Cake Island", "Under Siege", "The Straw Hats and Germa 66 become cornered in Bege's stronghold, and the full might of the Big Mom Pirates is pinning them down! It is only a matter of time before their defenses crack, so they take the only way out and make a break for it."),
            [(34, 25)] = new EpisodeInfo(34, 25, "Whole Cake Island", "You Can Do It, Caesar!!", "Caesar flies the entire alliance, still inside Bege, out of the venue! However, the full might of the Big Mom Pirates proves too much. The alliance’s annihilation is imminent, but something shakes the playing field."),
            [(34, 26)] = new EpisodeInfo(34, 26, "Whole Cake Island", "Become My Servant", "The Straw Hats continue their mad dash to the Sunny, and Pudding puts her plan to rebuild the cake into action. As Zeus pursues the Straw Hats, Nami keeps him occupied with a shocking surprise that might stop Big Mom in her tracks!"),
            [(34, 27)] = new EpisodeInfo(34, 27, "Whole Cake Island", "A Woman's Honor", "Big Mom is relentless in her pursuit of the Straw Hats. Elsewhere, the plan to recreate the wedding cake progresses while Pudding’s unpredictable heart confuses everyone, including herself."),
            [(34, 28)] = new EpisodeInfo(34, 28, "Whole Cake Island", "Mink Pedro, Captain of the Guardians", "Sandwiched between Big Mom and her advancing fleet, the only hope left to the Sunny is to blast right through it all. Meanwhile, Pedro comes face-to-face with his past and does what he must to ensure the success of his friends."),
            [(34, 29)] = new EpisodeInfo(34, 29, "Whole Cake Island", "Zero Escape", "In the Mirro-world, Luffy confronts the formidable Charlotte Katakuri, Big Mom’s strongest child. Meanwhile, Pudding and Sanji arrive at Cacao Island to remake the cake, but they bicker like a married couple the whole time."),
            [(34, 30)] = new EpisodeInfo(34, 30, "Whole Cake Island", "Wave Room", "Nami and Luffy discuss a rendezvous point while the others break the mirrors on the Sunny. Little do they know their plan is overheard, and Luffy’s grueling battle against Katakuri—haki vs. haki, rubber vs. mochi—intensifies."),
            [(34, 31)] = new EpisodeInfo(34, 31, "Whole Cake Island", "Merienda", "Katakuri retires to his sanctuary to enjoy a merienda—a sweet chance to replenish his haki! But he is not the only one munching away. A mochi mountain has crushed Luffy, and he has found a delectable way to escape."),
            [(34, 32)] = new EpisodeInfo(34, 32, "Whole Cake Island", "Way of Life", "When Chiffon comes under attack, Sanji must choose between defending her or revealing his whereabouts to his enemies. Elsewhere, Luffy uses Charlotte Brûlée as a human shield to avoid the wrath of Big Mom."),
            [(34, 33)] = new EpisodeInfo(34, 33, "Whole Cake Island", "Lion", "Sanji and Bege contend with Charlotte Oven, who boils the sea itself! Meanwhile, a vicious pincer attack bears down on the Straw Hats, but Carrot draws on a deep power within and springs to action under the light of the full moon."),
            [(34, 34)] = new EpisodeInfo(34, 34, "Whole Cake Island", "Unknown Mama", "Wielding Napoleon and backed by Zeus and Prometheus, Big Mom wreaks havoc on the Sunny. As the Straw Hats try to kick out the Emperor, their ship catches fire! But then, Jinbe steps up to the plate to take a swing."),
            [(34, 35)] = new EpisodeInfo(34, 35, "Whole Cake Island", "They Believe in Me", "The cake is finally brought before Big Mom. With the end of her vicious rampage in sight, the Straw Hats may just get their chance to flee! Big Mom's crew is forced to decide who to attack, and Luffy grows ever closer to mastering his haki."),
            [(34, 36)] = new EpisodeInfo(34, 36, "Whole Cake Island", "Pirate Luffy vs. Commander Katakuri", "Desiring revenge after complete embarrassment, Big Mom’s children band together to smash the Straw Hats. Meanwhile, Luffy and Katakuri are locked in a duel to end all duels, where each blow could be the last."),
            [(34, 37)] = new EpisodeInfo(34, 37, "Whole Cake Island", "One Last Thing", "Luffy's dramatic battle finally draws to a close, and a whole host of the Big Mom Pirates watch the last mirror to see who will come through."),
            [(34, 38)] = new EpisodeInfo(34, 38, "Whole Cake Island", "BAD END MUSICAL", "Sanji tries his best to save himself and his captain. Now, the fate of Totto Land rests on the taste of the wedding cake! With the help of Germa and the Sun Pirates, the Straw Hats see their chance to escape!"),
            [(34, 39)] = new EpisodeInfo(34, 39, "Whole Cake Island", "END ROLL", "As the Big Mom Pirate fleet approaches, Jinbe makes a crucial decision. At a great cost of energy and life, the Straw Hats make it out of Totto Land. Left behind in their wake are allies, friends, and would-be lovers."),
            [(35, 1)] = new EpisodeInfo(35, 1, "Reverie", "The Fifth Emperor", "Luffy’s reputation grows as all eyes look toward the Straw Hats’ next move. Meanwhile, the royals of the world make their way to the Reverie, but their absence welcomes a raid by a vicious band of pirates on the Kingdom of Lulusia."),
            [(35, 2)] = new EpisodeInfo(35, 2, "Reverie", "A Beautiful World", "At New Marineford, Fleet Admiral Sakazuki hears the news about Luffy while nobles from around the world gather for the Reverie! At the Red Line, Garp is escorting King Neptune’s family when Shirahoshi sees the surface world for the first time."),
            [(35, 3)] = new EpisodeInfo(35, 3, "Reverie", "The Empty Throne", "As the Reverie commences, Emperors Big Mom and Kaido both set their sights on Luffy! Meanwhile, a mysterious outsider visits the highest authority in the world, and the Revolutionary Army confirms its desire to save Kuma."),
            [(36, 1)] = new EpisodeInfo(36, 1, "Wano", "Onward to Wano", "Luffy and the others see familiar faces in a newspaper article about the Reverie right as they approach Wano. However, this dangerous and foreboding land is as inaccessible as it is mysterious, with the sea itself acting as a barrier!"),
            [(36, 2)] = new EpisodeInfo(36, 2, "Wano", "Seppuku", "A mysterious country, a rampaging slasher, an ancient samurai ritual of seppuku—and Zoro in the crosshairs! Stranded alone on the beach, Luffy tries to gather his bearings only to be confronted by some dangerous local fauna."),
            [(36, 3)] = new EpisodeInfo(36, 3, "Wano", "Amigasa Village", "Otama recounts her promise to Ace while Luffy rushes her to medical attention. But just as they reach the wasteland, a familiar face comes into view, and Kaido's underlings show up, too!"),
            [(36, 4)] = new EpisodeInfo(36, 4, "Wano", "Tsuru Returns the Favor", "Luffy and Zoro are stalled by Hawkins, but their fortune looks bright when a woman guides them to a teahouse. There, they find an herbal remedy for the river poison that Otama drank, while old acquaintances look on from a distance."),
            [(36, 5)] = new EpisodeInfo(36, 5, "Wano", "Okobore Town", "Otama is kidnapped by Kaido’s gifters and taken to a flourishing settlement called Bakura Town where a creepy-looking headliner was expecting her. Luffy, Zoro, and the samurai Okiku rush to her rescue."),
            [(36, 6)] = new EpisodeInfo(36, 6, "Wano", "Wano's Grand Sumo Tournament", "Luffy challenges Urashima, the strongest yokozuna in Wano, to a sumo match! However, Urashima is willing to resort to illegal moves. Meanwhile, Holdem tells Otama that he must punish her for a crime she committed."),
            [(36, 7)] = new EpisodeInfo(36, 7, "Wano", "The Provisions Ship", "Luffy and co. finally find Otama hanging from the mouth of a lion in Holdem’s stomach. Luffy prepares to save Otama from Kaido’s lackey, and elsewhere, a mysterious figure confronts Basil Hawkins."),
            [(36, 8)] = new EpisodeInfo(36, 8, "Wano", "The Ruins of Oden Castle", "Zoro and the others dash out of Bakura Town on the treasure ship filled with food! Luffy catches up to them, and together, they distribute food and water to the people of Okobore Town. Later, Law takes them to the ruins of a hilltop castle."),
            [(36, 9)] = new EpisodeInfo(36, 9, "Wano", "I Love Oden", "Kin’emon tells Luffy and the others about how Kozuki Oden brought peace to Kuri and became its daimyo. However, he was set up by Kaido and Orochi. When Kin’emon ran to save Oden’s wife, she revealed a shocking truth."),
            [(36, 10)] = new EpisodeInfo(36, 10, "Wano", "Shutenmaru", "Ashura Doji, a.k.a Shutenmaru, is one of the allies Kin’emon wants to search for, and he leads a raid on Okobore Town to steal the food that Luffy brought. This unrest prompts the drunken Kaido to fly above Okobore Town in his dragon form."),
            [(36, 11)] = new EpisodeInfo(36, 11, "Wano", "Emperor Kaido vs. Luffy", "Infuriated by Kaido’s violence toward his allies, Luffy lands blow after blow on the drunken Emperor. When he activates Gear Fourth and launches even more powerful attacks, Kaido fights back. Law tries to rescue Luffy but is interrupted by Hawkins."),
            [(36, 12)] = new EpisodeInfo(36, 12, "Wano", "Hah", "Luffy is sent to a great mine where prisoners must endure harsh, forced labor. There, he has an unexpected reunion. Meanwhile, Okiku rushes to the ruins of Oden Castle to confirm the safety of the Straw Hats and the others."),
            [(36, 13)] = new EpisodeInfo(36, 13, "Wano", "Void", "Perona prepares to leave Gloom Island after she finds out that her captain, Gecko Moria, is still alive. Meanwhile, Moria raids the infamous Pirate Island with his zombie army to retrieve his old friend from Blackbeard."),
            [(36, 14)] = new EpisodeInfo(36, 14, "Wano", "Otoko the Kamuro", "In the Prisoner Mining Camp, Luffy’s and Kid’s competition garners the wrong kind of attention. Meanwhile, Sanji’s soba becomes immensely popular, but gang members disturb his business and knock over a customer’s bowl."),
            [(36, 15)] = new EpisodeInfo(36, 15, "Wano", "Oiran Komurasaki Arrives", "Wano’s top courtesan—the oiran Komurasaki—is in procession and slowly makes her way to Orochi Palace. While everyone is fascinated by the oiran, some seeking vengeance get in her way."),
            [(36, 16)] = new EpisodeInfo(36, 16, "Wano", "Soba Mask", "In the Flower Capital, a member of the Beast Pirates, Page One, attacks every soba restaurant in search of Sanji. The Straw Hat’s cook faces Kaido’s headliner while using Germa’s device for the first time to hide his identity."),
            [(36, 17)] = new EpisodeInfo(36, 17, "Wano", "The Shogun and the Oiran", "At the shogun’s banquet, the oiran’s child attendant Otoko bursts with laughter and cannot stop. The enraged Shogun Orochi attacks her, but an unexpected person stands in his way. To end the to-do, Kyoshiro takes matters into his own hands."),
            [(36, 18)] = new EpisodeInfo(36, 18, "Wano", "Hyogoro the Flower", "Chopper’s group is off to rescue Luffy from the Prisoner Mining Camp, but the Straw Hat captain is in a bit of a bind. While Luffy tries to save old man Hyo from execution, Queen the Plague comes to visit the prison."),
            [(36, 19)] = new EpisodeInfo(36, 19, "Wano", "Gyukimaru of Bandit's Bridge", "Luffy fights in an endless sumo match to protect old man Hyo, and Nami, Robin, and Shinobu are cornered as Hawkins raids the bathhouse. Elsewhere, Zoro faces a samurai who accuses him of stealing Shusui, but their duel is interrupted by a woman’s scream."),
            [(36, 20)] = new EpisodeInfo(36, 20, "Wano", "A Woman's Secret", "The wounded Zoro wakes up to find out the shocking identity of the woman he saved and is told the unbelievable tale of twenty years earlier. Meanwhile, Luffy’s “training” is not getting anywhere, but a surprising person gives him his first lesson."),
            [(36, 21)] = new EpisodeInfo(36, 21, "Wano", "Sparks of Rebellion", "The smiling man Tonoyasu leaves all who met him mystified, what with his familiar terms of address and his shocking knowledge of the rebellion. In Udon prison, Luffy continues his new haki training, and during a brief respite, Raizo appears before him."),
            [(36, 22)] = new EpisodeInfo(36, 22, "Wano", "Shimotsuki Yasuie, the Daimyo of Hakumai", "Tonoyasu has been strung up and awaits his inevitable execution while all of Wano is shocked to find out who he really is. The citizens cry out in outrage, and the doomed man raises his voice in a final act of rebellion against Shogun Orochi."),
            [(36, 23)] = new EpisodeInfo(36, 23, "Wano", "SMILE", "The truth behind the smiles of the Ebisu villagers finally comes to light, the Shogun and Kaido’s plan revealed! And in Udon Luffy and Hyo are shocked to see Captain Kid return in chains alongside a smiling newcomer."),
            [(36, 24)] = new EpisodeInfo(36, 24, "Wano", "Olin", "A very big and hungry visitor arrives at Udon looking for Superstar Queen’s sweet red bean oshiruko, and Rasetsu town is in a full-blown riot with the Straw Hats at the center!"),
            [(36, 25)] = new EpisodeInfo(36, 25, "Wano", "Queen vs. Olin", "To Chopper’s surprise, the kind and sweet Olin—a.k.a. Emperor Big Mom—has proven her power to be just as devastating as ever. Olin’s hunger pangs just about reach their limit, and Luffy must act quickly before his mechanical collar executes him."),
            [(36, 26)] = new EpisodeInfo(36, 26, "Wano", "Mummy", "Babanuki unleashes an unthinkable weapon, and a mysterious figure emerges from a forbidden cell to save Luffy. Then, Old Man Hyo emboldens Raizo and Kiku to reveal their true identities and give the desperate prisoners some much-needed hope."),
            [(36, 27)] = new EpisodeInfo(36, 27, "Wano", "A Soldier's Dream", "In Udon, an unexpected person stands up to bring the prisoners renewed hope! Elsewhere, Zoro and Hiyori are pursued by ninja, Orochi is told of Trafalgar Law’s capture, and the people of the Flower Capital prepare for the upcoming Fire Festival."),
            [(36, 28)] = new EpisodeInfo(36, 28, "Wano", "RAMPAGE", "Kanjuro takes Yasuie’s body to bury him with Oden, and Robin, Usopp, and Brook cleverly pass a checkpoint. While Big Mom and Kaido clash at Onigashima, Old Man Hyo finds his former allies in Udon. And in Ringo, Zoro faces Gyukimaru once more."),
            [(36, 29)] = new EpisodeInfo(36, 29, "Wano", "Once a Fox", "Zoro runs after the warrior-monk who took Shusui, and Kawamatsu shares his memory of a fox that was guarding the graves in Ringo. Soon, they find a room filled with swords, and Hiyori tells Zoro to give Shusui back to the land of Wano."),
            [(36, 30)] = new EpisodeInfo(36, 30, "Wano", "Enma", "As the day of the raid approaches, Kin’emon discusses the plan, and at Onigashima, Kaido and Big Mom make a surprising declaration! In the meantime, Zoro is stunned by Enma’s demonic power and its ties to him."),
            [(36, 31)] = new EpisodeInfo(36, 31, "Wano", "Big News", "The resolution that passed during the Reverie and the incidents surrounding the conference stun people all over the world and corner the Seven Warlords of the Sea. Meanwhile, Captain Koby secretly contacts someone currently in Wano."),
            [(36, 32)] = new EpisodeInfo(36, 32, "Wano", "Ultimate", "Admiral Issho and Fleet Admiral Sakazuki argue over the abolition of the Seven Warlord system and the alliance between two Emperors. Meanwhile, ex-Fleet Admiral Sengoku teaches the young Marines about the legendary Rocks Pirates."),
            [(36, 33)] = new EpisodeInfo(36, 33, "Wano", "The Designated Port", "On the day of the Fire Festival, Orochi heads for Onigashima where a big banquet is being held, and the Red Scabbards meet at the Tokage Port rendezvous point. However, Kin'emon and his compatriots are stunned that none of their allies are there."),
            [(36, 34)] = new EpisodeInfo(36, 34, "Wano", "The Day of the Mountain God", "Forty-one years ago, Oden had not yet earned the respect of everyone, but when the angry mountain god almost killed Kin’emon, Oden stepped in. Still, disavowed by his father, Oden journeyed around the country, gathering followers as he went."),
            [(36, 35)] = new EpisodeInfo(36, 35, "Wano", "Daimyo and Retainers", "Oden transformed Kuri and was made Daimyo over it. He also met a kappa fishman and two minks, who soon became his followers. Kin’emon and the other vassals took Yasuie's strong words to heart and began transforming themselves for their lord."),
            [(36, 36)] = new EpisodeInfo(36, 36, "Wano", "Oden's Adventure", "As Oden began his voyage, he was in awe of all the things that he had never seen before, and for the first time in his life, he became a wanted person and made the headlines, catching the eye of a high-profile figure."),
            [(36, 37)] = new EpisodeInfo(36, 37, "Wano", "Roger and Whitebeard", "Back in Wano, the Shogun Sukiyaki seemed to fall critically ill and named Orochi proxy Shogun. Meanwhile, Whitebeard's crew made landfall where the Roger Pirates were fighting against the Marines on the other side of the island."),
            [(36, 38)] = new EpisodeInfo(36, 38, "Wano", "Roger's Adventure", "Oden visits Wano and sees his retainers for the first time in years, but he immediately returns to the sea and leaves his family behind in their care. After stopping at Zou for the final red poneglyph, the Roger Pirates finally make it to the last island."),
            [(36, 39)] = new EpisodeInfo(36, 39, "Wano", "Fool of a Lord", "The Roger Pirates have completed their grand adventure, and the King of the Pirates is born! Gol D. Roger disbands his merry crew and Oden returns triumphant to his homeland of Wano. However, a sinister surprise awaits him upon his rightful throne."),
            [(36, 40)] = new EpisodeInfo(36, 40, "Wano", "Oden Was Meant to Boil", "Oden and his retainers are sentenced to death by boiling in a pot. Oden asks for a chance to survive, and an amused Kaido accepts. Meanwhile, Shinobu reveals to surprised citizens what Oden had been doing to save the country."),
            [(36, 41)] = new EpisodeInfo(36, 41, "Wano", "The Kozuki Clan", "After Kaido attacks Oden's family in their castle, Toki uses her power one last time and makes a prophecy to the people of Wano. Twenty years later, the raid on Onigashima is imminent, but the sting of betrayal hits hardest on the eve of triumph!"),
            [(36, 42)] = new EpisodeInfo(36, 42, "Wano", "Kin'emon's Clever Ruse", "As the Scabbards and their comrades struggle to rescue the kidnapped Momo, two unforseen allies join the fight: one thought long gone 20 years ago and another pledging his loyalty to Luffy."),
            [(36, 43)] = new EpisodeInfo(36, 43, "Wano", "The Party's Off!!!", "The triumphant return of Jinbe sees him officially join the Straw Hats as their helmsman. As the Ninja-Pirate-Mink-Samurai Alliance plans the first steps of the raid on Onigashima, the Straw Hats look for a way to celebrate their new crewmate."),
            [(36, 44)] = new EpisodeInfo(36, 44, "Wano", "A Family Matter", "The raid on Onigashima kicks off with Law's group sneaking around the back and Luffy's group moving straight through the front door, cleverly disguised as the enemy itself! Meanwhile, Kaido holds an audience with the Soaring Six."),
            [(36, 45)] = new EpisodeInfo(36, 45, "Wano", "Fighting Music", "Luffy and Zoro are outed by Scratchman Apoo, and Queen announces that anyone who catches them will get a coveted promotion. Elsewhere, Kin’emon's infiltration strategy evolves while Big Mom's children are greeted by a surprise party crasher."),
            [(36, 46)] = new EpisodeInfo(36, 46, "Wano", "Thunder", "Kanjuro arrives before Orochi and Kaido with Momonosuke in tow and Big Mom chases Chopper and Usopp. Meanwhile, Luffy butts heads with Page One's doting sister Ulti, but unbeknownst to Straw Hat, he's also about to meet Kaido's runaway son..."),
            [(36, 47)] = new EpisodeInfo(36, 47, "Wano", "My Bible", "Yamato notices something in Luffy that reminds him of someone from long ago. When Orochi declares he will execute Momonosuke, Kaido adds to the excitement with a big announcement, and at the rear of the mansion, the Red Scabbards reunite!"),
            [(36, 48)] = new EpisodeInfo(36, 48, "Wano", "Retainers of Glory", "The stage is set, the curtain is drawn, and the Red Scabbards approach their target of 20 years. Luffy and Yamato burst onto the Performance Floor with a bang, and Wano's samurai make the opening attack for the long-anticipated battle."),
            [(36, 49)] = new EpisodeInfo(36, 49, "Wano", "I Can't Imagine Losing", "Momonosuke is finally freed, and King and Queen must contend with the incursion of samurai and pirates on the Performance Floor. Big Mom is swiftly removed from the situation, and the Straw Hat pirates gather once more as the assualt kicks off!"),
            [(36, 50)] = new EpisodeInfo(36, 50, "Wano", "Army of One", "Luffy invokes memories of the Straw Hats pirates' battle against Oars as he topples one of the gargantuan Numbers... with the help of an unforeseen ally! Meanwhile, Queen readies a demonic secret weapon that will ice over the samurai invasion."),
            [(36, 51)] = new EpisodeInfo(36, 51, "Wano", "Remnants", "Yamato continues to pursue Momonosuke as Shinobu scurries with him through the halls of Onigashima. Meanwhile, the battle on the roof intensifies as the Scabbards fight with honor to prove they are not just cheap imitations of the great Kozuki Oden."),
            [(36, 52)] = new EpisodeInfo(36, 52, "Wano", "A Kunoichi's Oath", "Friend and foe alike fall victim to Queen's chilling virus, and Apoo is playing keep-away with the vial of antibodies. Perospero and Marco's brief alliance is shattered, and so are Nami and Usopp's skulls as they face down Ulti and Page One."),
            [(36, 53)] = new EpisodeInfo(36, 53, "Wano", "Flame Clouds", "In the midst of the chaos on the Performance Floor, Chopper learns the secret behind Queen's latest plague. Meanwhile, Big Mom reaches the roof, Sanji hurries in the name of love, and Zoro watches as something falls from the hole in the ceiling."),
            [(36, 54)] = new EpisodeInfo(36, 54, "Wano", "The Sake I Brewed to Drink With You", "Sanji gets himself caught in Black Maria's web, Jinbe faces Who's-Who, and Yamato tells of the time Ace visited Onigashima. Elsewhere, Marco joins the fray on the Performance Floor to help soothe the infected and to offer Zoro a ride."),
            [(36, 55)] = new EpisodeInfo(36, 55, "Wano", "Straw Hat Luffy", "Yamato recalls learning the sad truth about Ace, but remembers what he said about his brother, Straw Hat Luffy. As Kaido and Big Mom join forces on the roof of Onigashima, they are faced with the biggest names of the Worst Generation..."),
            [(36, 56)] = new EpisodeInfo(36, 56, "Wano", "The Battle of Monsters on Onigashima", "It's a fight to remember! Five members from the Worst Generation—Monkey D. Luffy, Eustass \"Captain\" Kid, Trafalgar Law, Roronoa Zoro, and Killer—facing off against two Emperors—Kaido, \"King of the Beasts,\" and Charlotte Linlin, a.k.a. Big Mom!"),
            [(36, 57)] = new EpisodeInfo(36, 57, "Wano", "Millet Dumplings", "Luffy momentarily topples Kaido with Gear Fourth, but the effort depletes his haki. Inside Onigashima, Tama's plan to rid the country of Kaido and Orochi unfolds as she feeds the gifters her special millet dumplings and turns the tide of battle!"),
            [(37, 1)] = new EpisodeInfo(37, 1, "Egghead", "New Emperors", "The Straw Hats rave over new bounties, and Crocodile and Mihawk pay Buggy a visit. Elsewhere, Dragon and his revolutionaries are shocked when they read about Sabo in the news, and an old comrade returns, a shell of his former self."),
            [(37, 2)] = new EpisodeInfo(37, 2, "Egghead", "The Matter Involving Captain Koby", "The three-way struggle between the Marines, the Blackbeard Pirates, and the Kuja Pirates comes to an end when an unexpected figure shows up. On the Sunny, the Straw Hats talk about the recent incidents around the world and Luffy’s words surprise everyone."),
            [(37, 3)] = new EpisodeInfo(37, 3, "Egghead", "Future Island Egghead", "Sabo contacts his comrades, which prompts a certain eavesdropper to take action against an entire island nation! Meanwhile, Luffy, Chopper, and Jinbe alongside a mysterious girl become separated from the rest of the Straw Hats when a giant shark attacks."),
            [(37, 4)] = new EpisodeInfo(37, 4, "Egghead", "Adventure in the Land of Science", "Franky and Luffy's groups are split up, but each encounter a person claiming to be Dr. Vegapunk. On the island, Luffy and co. find convenient food and futuristic clothing, but when a pacifista shows up, Bonney reveals her shocking relationship with Kuma."),
            [(37, 5)] = new EpisodeInfo(37, 5, "Egghead", "The Six Vegapunks", "Lilith takes the Sunny and its remaining crew to the laboratory in the clouds, where a new threat lurking within its corridors attacks unexpectedly. As the battle ensues, Vegapunk's satellites look on with excitement."),
            [(37, 6)] = new EpisodeInfo(37, 6, "Egghead", "The Will of Ohara", "Vegapunk Shaka shares his theory about an ancient kingdom and the Void Century. He also reminisces about the foundation of Dragon's Revolutionary Army. Meanwhile, Luffy and Chopper try to activate the giant robot when something surprising happens."),
            [(37, 7)] = new EpisodeInfo(37, 7, "Egghead", "Punk Records", "After Vegapunk shares his dream with Luffy's group, he asks the Straw Hat captain for a big favor. Meanwhile, CP0 ignores their lack of invitation to Egghead and get around the island defenses to make landfall."),
            [(37, 8)] = new EpisodeInfo(37, 8, "Egghead", "The Strongest Form of Humanity", "As the rematch between Luffy and Lucci kicks off, Dr. Vegapunk notices something very fascinating about the Straw Hat captain's new abilities. Meanwhile, Sentomaru arrives with the latest pacifista models as a very unlikely ally to the pirates!"),
            [(37, 9)] = new EpisodeInfo(37, 9, "Egghead", "The Weight of Memory", "To avenge her father, Bonney chases Dr. Vegapunk through the lab, but a familiar symbol catches her eye. As she investigates, the satellites call in an unexpected favor to finally deal with CP0's rampage outside the lab."),
            [(37, 10)] = new EpisodeInfo(37, 10, "Egghead", "Miss Buckingham Stussy", "Upon mysterious orders, Stussy betrays Lucci and Kaku. Lilith and Edison also hurry outside the lab to stop the rampaging seraphim. Meanwhile, the main Dr. Vegapunk goes missing, and Admiral Kizaru escorts a powerful and commanding entity toward Egghead."),
            [(37, 11)] = new EpisodeInfo(37, 11, "Egghead", "Labophase Death Game", "The search for Dr. Vegapunk's main body is threatened by the deadly seraphim, only this time the satellites' orders are unable to pacify them. Faced with a tough decision, Luffy and Zoro must team up with the most unlikely of allies."),
            [(37, 12)] = new EpisodeInfo(37, 12, "Egghead", "Escape Limit", "Luffy, Zoro, Lucci, and Kaku combine their might, and a chivalrous cook rescues Nami from near defeat. Meanwhile, Lilith, Usopp, and Franky are stopped in their tracks by someone very deadly, yet very cute."),
            [(37, 13)] = new EpisodeInfo(37, 13, "Egghead", "The Emperor's Crew, The Red Hair Pirates", "Eustass \"Captain\" Kid sails in, guns blazing against Elbaph while Shanks of the Red Hair Pirates defends his crew and friends... without an ounce of mercy."),
            [(37, 14)] = new EpisodeInfo(37, 14, "Egghead", "The Legendary Hero", "While Koby desperately attempts to flee Beehive, Cross Guild's bounty entices the hundreds of pirates there to hunt him down. But before they can seal his fate and claim the prize, a familiar Marine warship descends from the sky!"),
            [(37, 15)] = new EpisodeInfo(37, 15, "Egghead", "Let's Go and Take It!!", "While Cross Guild grows into a serious concern for the Marines, Buggy makes an impassioned speech to spur his crew into a zealous fervor. Additionally, Sabo returns to his comrades and begins his report on the events that happened at the Reverie."),
            [(37, 16)] = new EpisodeInfo(37, 16, "Egghead", "The Attempted Murder of a Celestial Dragon", "During the Reverie, Sabo and Bonney met and worked together toward the same goal. King Cobra also met with the Five Elders to ask some hard questions, in the process learning the dark truth of the world."),
            [(37, 17)] = new EpisodeInfo(37, 17, "Egghead", "The Five Elders", "At the time of the Reverie, Wapol overheard something he shouldn't have and fled the Holy Land with an unlikely partner. On the heels of these events, Imu ordered the Five Elders to test a devastating weapon engineered by Dr. Vegapunk!"),
            [(37, 18)] = new EpisodeInfo(37, 18, "Egghead", "The Final Lesson", "At Pirate Island, Vice Admiral Garp continues the Koby extraction mission, but Avalo Pizarro's devil fruit powers threaten to sink the rescue ship. Garp, weakened from an attack by Shiryu, entrusts the operation's success to the next generation."),
            [(37, 19)] = new EpisodeInfo(37, 19, "Egghead", "Hostage Situation", "In the Labophase on Egghead, which is completely surrounded by the Marines, Luffy and the others devise a plan to escape the island. However, the Frontier Dome, the laser defense system, cannot be deactivated without York’s secret passcode."),
            [(37, 20)] = new EpisodeInfo(37, 20, "Egghead", "Sentomaru", "Admiral Kizaru finally makes landfall, but Sentomaru stands in his way while the Straw Hats attempt to move the Sunny to the back side of the island. And back at Mary Geoise, Fleet Admiral Akainu stands in the way of the ever-advancing Kuma."),
            [(37, 21)] = new EpisodeInfo(37, 21, "Egghead", "Luffy vs. Kizaru", "Luffy faces off in a spectacular aerial battle against Admiral Kizaru, but this time, Straw Hat is 100 times stronger than before. Unbeknownst to those on Egghead, something ominous is about to make an appearance on the island..."),
            [(37, 22)] = new EpisodeInfo(37, 22, "Egghead", "Kumachi", "Born with the rare blood of the Buccaneer people, young Kuma was fated to lead a life of persecution at the hands of the Celestial Dragons, but his father instilled hope within him through the legend of Nika and the Drums of Liberation."),
            [(37, 23)] = new EpisodeInfo(37, 23, "Egghead", "Ginny", "Kuma lived with Ginny in his old village until the injustices of the world pushed them to join Iva and Dragon as revolutionaries. However, when tragedy befell Ginny, Kuma found a new purpose in life in the newborn she left behind."),
        };

        public static string NormalizeArcTitle(string? input)
        {
            if (string.IsNullOrWhiteSpace(input)) return string.Empty;
            var s = input.Trim().Trim('"', '\'');
            s = s.Replace('_', ' ').Replace('.', ' ').Replace('-', ' ');
            while (s.Contains("  ", StringComparison.Ordinal)) s = s.Replace("  ", " ", StringComparison.Ordinal);
            return s.Trim().ToLowerInvariant();
        }
    }
}
